import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

function NotificationPage() {
  const [notifications, setNotifications] = useState([]);
  const [targetPatients, setTargetPatients] = useState([]);
  const [newNotification, setNewNotification] = useState({
    notificationMessage: {
      title: '',
      message: '',
    },
    notificationCriteria: {
      startAge: '',
      endAge: '',
      gender: '',
    },
  });

  // Bildirimleri ve hedef hastaları çekmek için useEffect kullanımı
  useEffect(() => {
    fetch('http://localhost:8081/api/notification/getall')
      .then((response) => response.json())
      .then((data) => setNotifications(data))
      .catch((error) => console.error('Error fetching notifications:', error));

    fetch('http://localhost:8081/api/notification/getAllTargetPatient')
      .then((response) => response.json())
      .then((data) => setTargetPatients(data))
      .catch((error) => console.error('Error fetching target patients:', error));
  }, []);

  // Yeni bildirim ekleme fonksiyonu
  const handleAddNotification = () => {
    fetch('http://localhost:8081/api/notification/addNotification', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(newNotification),
    })
      .then((response) => response.json())
      .then((data) => {
        setNotifications([...notifications, data]);
        setNewNotification({
          notificationMessage: { title: '', message: '' },
          notificationCriteria: { startAge: '', endAge: '', gender: '' },
        });
      })
      .catch((error) => console.error('Error adding notification:', error));
  };

  // Bildirim silme fonksiyonu
  const handleDeleteNotification = (id) => {
    fetch(`http://localhost:8081/api/notification/delete/${id}`, {
      method: 'DELETE',
    })
      .then(() => {
        setNotifications(notifications.filter((notification) => notification.id !== id));
      })
      .catch((error) => console.error('Error deleting notification:', error));
  };

  return (
    <div>
      <Link to="/" style={{ float: 'right', fontSize: '18px', marginRight: '20px' }}>
        Go back to Hospital Page
      </Link>
      <h1>Hello, this is the Notification Page!</h1>

      {/* Bildirim Ekleme Formu */}
      <h2>Add New Notification</h2>
      <div style={{ marginBottom: '20px' }}>
        <label>
          Title:
          <input
            type="text"
            value={newNotification.notificationMessage.title}
            onChange={(e) =>
              setNewNotification({
                ...newNotification,
                notificationMessage: { ...newNotification.notificationMessage, title: e.target.value },
              })
            }
          />
        </label>
        <label>
          Message:
          <input
            type="text"
            value={newNotification.notificationMessage.message}
            onChange={(e) =>
              setNewNotification({
                ...newNotification,
                notificationMessage: { ...newNotification.notificationMessage, message: e.target.value },
              })
            }
          />
        </label>
        <label>
          Start Age:
          <input
            type="number"
            value={newNotification.notificationCriteria.startAge}
            onChange={(e) =>
              setNewNotification({
                ...newNotification,
                notificationCriteria: { ...newNotification.notificationCriteria, startAge: e.target.value },
              })
            }
          />
        </label>
        <label>
          End Age:
          <input
            type="number"
            value={newNotification.notificationCriteria.endAge}
            onChange={(e) =>
              setNewNotification({
                ...newNotification,
                notificationCriteria: { ...newNotification.notificationCriteria, endAge: e.target.value },
              })
            }
          />
        </label>
        <label>
          Gender:
          <input
            type="text"
            value={newNotification.notificationCriteria.gender}
            onChange={(e) =>
              setNewNotification({
                ...newNotification,
                notificationCriteria: { ...newNotification.notificationCriteria, gender: e.target.value },
              })
            }
          />
        </label>
        <button onClick={handleAddNotification}>Add Notification</button>
      </div>

      {/* İlk Tablo: Bildirimler */}
      <h2>Notifications</h2>
      <table border="1" style={{ width: '100%', textAlign: 'left', marginTop: '20px' }}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Title</th>
            <th>Message</th>
            <th>Start Age</th>
            <th>End Age</th>
            <th>Gender</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {notifications.map((notification) => (
            <tr key={notification.id}>
              <td>{notification.id}</td>
              <td>{notification.notificationMessage.title}</td>
              <td>{notification.notificationMessage.message}</td>
              <td>{notification.notificationCriteria.startAge}</td>
              <td>{notification.notificationCriteria.endAge}</td>
              <td>{notification.notificationCriteria.gender}</td>
              <td>
                <button onClick={() => handleDeleteNotification(notification.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* İkinci Tablo: Hedef Hastalar */}
      <h2>Target Patients</h2>
      <table border="1" style={{ width: '100%', textAlign: 'left', marginTop: '20px' }}>
        <thead>
          <tr>
            <th>Patient ID</th>
            <th>Notification ID</th>
            <th>Primary Phone</th>
            <th>Primary Mail</th>
          </tr>
        </thead>
        <tbody>
          {targetPatients.map((patient) => (
            <tr key={patient.patientId}>
              <td>{patient.patientId}</td>
              <td>{patient.notificationId}</td>
              <td>{patient.primaryPhone}</td>
              <td>{patient.primaryMail}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default NotificationPage;
