import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const HospitalPage = () => {
  const [patients, setPatients] = useState([]);
  const [newPatient, setNewPatient] = useState({
    firstName: '',
    middleName: '',
    lastName: '',
    dateOfBirth: '',
    gender: '',
    address: '',
    tckn: '',
    passportNumber: '',
    isSmsActive: false,
    isEmailActive: false,
    phoneNumbers: [],
    emailAddresses: []
  });

  const [errors, setErrors] = useState({});

  useEffect(() => {
    fetchPatients();
  }, []);

  const fetchPatients = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/patient/getall');
      setPatients(response.data);
      console.log(response.data);
    } catch (error) {
      console.error('Error fetching patients:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewPatient(prevState => ({ ...prevState, [name]: value }));
        // TCKN alanında sadece sayıların girilmesine izin ver
        if (name === 'tckn' && isNaN(value)) {
            return;
          }
  };

  const handleCheckboxChange = (e) => {
    const { name, checked } = e.target;
    setNewPatient(prevState => ({ ...prevState, [name]: checked }));
  };

  const validate = () => {
    let errors = {};

    if (!newPatient.firstName.trim()) {
      errors.firstName = 'First Name is required';
    }

    if (!newPatient.lastName.trim()) {
      errors.lastName = 'Last Name is required';
    }

    if (!newPatient.dateOfBirth) {
      errors.dateOfBirth = 'Date of Birth is required';
    }

    if (!newPatient.gender || !/^[A-Za-z]$/.test(newPatient.gender)) {
      errors.gender = 'Gender must be a single letter';
    }

    if (!newPatient.address.trim()) {
      errors.address = 'Address is required';
    }

    if (!newPatient.tckn || !/^\d{1,11}$/.test(newPatient.tckn)) {
      errors.tckn = 'TCKN must be a number up to 11 digits';
    }

    setErrors(errors);

    return Object.keys(errors).length === 0;
  };

  const handleAddPatient = async () => {
    if (!validate()) {
      return;
    }

    try {
      const phoneNumbersArray = newPatient.phoneNumbers
        ? newPatient.phoneNumbers.split(',').map(number => ({ phoneNumber: number.trim() }))
        : [];
      const emailAddressesArray = newPatient.emailAddresses
        ? newPatient.emailAddresses.split(',').map(email => ({ emailAddress: email.trim() }))
        : [];

      const patientToAdd = {
        ...newPatient,
        phoneNumbers: phoneNumbersArray,
        emailAddresses: emailAddressesArray
      };

      const response = await axios.post('http://localhost:8080/api/patient/addPatient', patientToAdd);
      setPatients([...patients, response.data]);
      setNewPatient({
        firstName: '',
        middleName: '',
        lastName: '',
        dateOfBirth: '',
        gender: '',
        address: '',
        tckn: '',
        passportNumber: '',
        isSmsActive: false,
        isEmailActive: false,
        phoneNumbers: '',
        emailAddresses: ''
      });
      setErrors({});
    } catch (error) {
      console.error('Error adding patient:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:8080/api/patient/delete/${id}`);
      setPatients(patients.filter(patient => patient.id !== id));
    } catch (error) {
      console.error('Error deleting patient:', error);
    }
  };

  return (
    <div>
      <Link to="/notifications" style={{ float: 'right', fontSize: '18px', marginRight: '20px' }}>
        Go to Notification Page
      </Link>

      <h1>Hospital Patient Management</h1>

      {/* Add Patient Form */}
      <div style={{ marginBottom: '20px' }}>
        <h2>Add New Patient</h2>
        <div>
          <input
            type="text"
            name="firstName"
            placeholder="First Name"
            value={newPatient.firstName}
            onChange={handleInputChange}
            style={errors.firstName ? { borderColor: 'red' } : {}}
          />
          {errors.firstName && <span style={{ color: 'red', marginLeft: '10px' }}>{errors.firstName}</span>}
        </div>

        <div>
          <input
            type="text"
            name="middleName"
            placeholder="Middle Name"
            value={newPatient.middleName}
            onChange={handleInputChange}
          />
        </div>

        <div>
          <input
            type="text"
            name="lastName"
            placeholder="Last Name"
            value={newPatient.lastName}
            onChange={handleInputChange}
            style={errors.lastName ? { borderColor: 'red' } : {}}
          />
          {errors.lastName && <span style={{ color: 'red', marginLeft: '10px' }}>{errors.lastName}</span>}
        </div>

        <div>
          <input
            type="date"
            name="dateOfBirth"
            placeholder="Date of Birth"
            value={newPatient.dateOfBirth}
            onChange={handleInputChange}
            style={errors.dateOfBirth ? { borderColor: 'red' } : {}}
          />
          {errors.dateOfBirth && <span style={{ color: 'red', marginLeft: '10px' }}>{errors.dateOfBirth}</span>}
        </div>

        <div>
          <input
            type="text"
            name="gender"
            placeholder="Gender"
            value={newPatient.gender}
            onChange={handleInputChange}
            style={errors.gender ? { borderColor: 'red' } : {}}
          />
          {errors.gender && <span style={{ color: 'red', marginLeft: '10px' }}>{errors.gender}</span>}
        </div>

        <div>
          <input
            type="text"
            name="address"
            placeholder="Address"
            value={newPatient.address}
            onChange={handleInputChange}
            style={errors.address ? { borderColor: 'red' } : {}}
          />
          {errors.address && <span style={{ color: 'red', marginLeft: '10px' }}>{errors.address}</span>}
        </div>

        <div>
          <input
            type="text"
            name="tckn"
            placeholder="TCKN"
            value={newPatient.tckn}
            onChange={handleInputChange}
            style={errors.tckn ? { borderColor: 'red' } : {}}
          />
          {errors.tckn && <span style={{ color: 'red', marginLeft: '10px' }}>{errors.tckn}</span>}
        </div>

        <div>
          <input
            type="text"
            name="passportNumber"
            placeholder="Passport Number"
            value={newPatient.passportNumber}
            onChange={handleInputChange}
          />
        </div>

        <div>
          <label>
            <input
              type="checkbox"
              name="isSmsActive"
              checked={newPatient.isSmsActive}
              onChange={handleCheckboxChange}
            />
            SMS Notifications
          </label>
          <label>
            <input
              type="checkbox"
              name="isEmailActive"
              checked={newPatient.isEmailActive}
              onChange={handleCheckboxChange}
            />
            Email Notifications
          </label>
        </div>

        <div>
          <textarea
            name="phoneNumbers"
            placeholder="Phone Numbers (comma separated)"
            value={newPatient.phoneNumbers}
            onChange={handleInputChange}
          />
        </div>

        <div>
          <textarea
            name="emailAddresses"
            placeholder="Email Addresses (comma separated)"
            value={newPatient.emailAddresses}
            onChange={handleInputChange}
          />
        </div>

        <button onClick={handleAddPatient}>Add Patient</button>
      </div>

      {/* Patient Table */}
      <table border="1" style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th>ID</th>
            <th>First Name</th>
            <th>Middle Name</th>
            <th>Last Name</th>
            <th>Date of Birth</th>
            <th>Gender</th>
            <th>Address</th>
            <th>TCKN</th>
            <th>Passport Number</th>
            <th>Phone Numbers</th>
            <th>Email Addresses</th>
            <th>SMS Active</th>
            <th>Email Active</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {patients.map(patient => (
            <tr key={patient.id}>
              <td>{patient.id}</td>
              <td>{patient.firstName}</td>
              <td>{patient.middleName}</td>
              <td>{patient.lastName}</td>
              <td>{patient.dateOfBirth}</td>
              <td>{patient.gender}</td>
              <td>{patient.address}</td>
              <td>{patient.tckn}</td>
              <td>{patient.passportNumber}</td>
              <td>{patient.phoneNumbers.map(pn => pn.phoneNumber).join(', ')}</td>
              <td>{patient.emailAddresses.map(ea => ea.emailAddress).join(', ')}</td>
              <td>{patient.isSmsActive ? 'Yes' : 'No'}</td>
              <td>{patient.isEmailActive ? 'Yes' : 'No'}</td>
              <td>
                <button onClick={() => handleDelete(patient.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default HospitalPage;
