import React, { useState, useEffect } from 'react';
import axios from 'axios';

const HospitalPage = () => {
    const [patients, setPatients] = useState([]);
    const [newPatient, setNewPatient] = useState({ firstName: '', lastName: '', tckn: '' });

    useEffect(() => {
        fetchPatients();
    }, []);

    const fetchPatients = async () => {
        try {
            const response = await axios.get('http://localhost:8080/api/patient/getall');
            setPatients(response.data);
        } catch (error) {
            console.error('Error fetching patients:', error);
        }
    };

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:8080/api/patient/delete/${id}`);
            setPatients(patients.filter(patient => patient.id !== id));
        } catch (error) {
            console.error('Error deleting patient:', error);
        }
    };

    const handleAddPatient = async () => {
        try {
            const response = await axios.post('http://localhost:8080/api/patient/add', newPatient);
            setPatients([...patients, response.data]);
            setNewPatient({ firstName: '', lastName: '', tckn: '' });
        } catch (error) {
            console.error('Error adding patient:', error);
        }
    };

    return (
        <div>
            <h1>Hospital Patient Management</h1>
            
            {/* Add Patient Form */}
            <div style={{ marginBottom: '20px' }}>
                <h2>Add New Patient</h2>
                <input
                    type="text"
                    placeholder="First Name"
                    value={newPatient.firstName}
                    onChange={(e) => setNewPatient({ ...newPatient, firstName: e.target.value })}
                />
                <input
                    type="text"
                    placeholder="Last Name"
                    value={newPatient.lastName}
                    onChange={(e) => setNewPatient({ ...newPatient, lastName: e.target.value })}
                />
                <input
                    type="text"
                    placeholder="TCKN"
                    value={newPatient.tckn}
                    onChange={(e) => setNewPatient({ ...newPatient, tckn: e.target.value })}
                />
                <button onClick={handleAddPatient}>Add Patient</button>
            </div>

            {/* Patient Table */}
            <table border="1" style={{ width: '100%', borderCollapse: 'collapse' }}>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>First Name</th>
                        <th>Last Name</th>
                        <th>TCKN</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {patients.map((patient) => (
                        <tr key={patient.id}>
                            <td>{patient.id}</td>
                            <td>{patient.firstName}</td>
                            <td>{patient.lastName}</td>
                            <td>{patient.tckn}</td>
                            <td>
                                <button onClick={() => handleDelete(patient.id)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default HospitalPage;
