import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const HospitalPage = () => {
  const [patients, setPatients] = useState([]);
  const [newPatient, setNewPatient] = useState({
    firstName: '',
    middleName: '',
    lastName: '',
    dateOfBirth: '',
    gender: '',
    address: '',
    tckn: '',
    passportNumber: '',
    isSmsActive: false,
    isEmailActive: false,
    phoneNumbers: [],
    emailAddresses: []
  });
  const [errors, setErrors] = useState({});
  const [popupMessage, setPopupMessage] = useState('');
  const [searchQuery, setSearchQuery] = useState(''); // Added state for search query

  useEffect(() => {
    fetchPatients();
  }, []);

  const fetchPatients = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/patient/getall');
      setPatients(response.data);
    } catch (error) {
      console.error('Error fetching patients:', error);
    }
  };
  
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name === 'tckn' && isNaN(value)) return;
    setNewPatient(prevState => ({ ...prevState, [name]: value }));
  };

  const handlePhoneNumberChange = (index, e) => {
    const { name, value } = e.target;
    const updatedPhoneNumbers = [...newPatient.phoneNumbers];
    updatedPhoneNumbers[index][name] = value;
    setNewPatient(prevState => ({ ...prevState, phoneNumbers: updatedPhoneNumbers }));
  };

  const handleEmailAddressChange = (index, e) => {
    const { name, value } = e.target;
    const updatedEmailAddresses = [...newPatient.emailAddresses];
    updatedEmailAddresses[index][name] = value;
    setNewPatient(prevState => ({ ...prevState, emailAddresses: updatedEmailAddresses }));
  };

  const handleCheckboxChange = (e) => {
    const { name, checked } = e.target;
    setNewPatient(prevState => ({ ...prevState, [name]: checked }));
  };

  const validateFields = () => {
    let tempErrors = {};
    if (!newPatient.firstName) tempErrors.firstName = 'First Name is required';
    if (!newPatient.lastName) tempErrors.lastName = 'Last Name is required';
    if (!newPatient.dateOfBirth) tempErrors.dateOfBirth = 'Date of Birth is required';
    if (!newPatient.gender || newPatient.gender.length !== 1) tempErrors.gender = 'Gender must be 1 character';
    if (!newPatient.address) tempErrors.address = 'Address is required';
    if (!newPatient.tckn || newPatient.tckn.length !== 11) tempErrors.tckn = 'TCKN must be 11 digits';
    if (!newPatient.passportNumber) tempErrors.passportNumber = 'Passport Number is required';
    setErrors(tempErrors);

    return Object.keys(tempErrors).length === 0;
  };

  const validateFieldsForUpdate = () => {
    let tempErrors = {};
    if (!newPatient.firstName) tempErrors.firstName = 'First Name is required';
    if (!newPatient.lastName) tempErrors.lastName = 'Last Name is required';
    if (!newPatient.gender || newPatient.gender.length !== 1) tempErrors.gender = 'Gender must be 1 character';
    if (!newPatient.address) tempErrors.address = 'Address is required';
    setErrors(tempErrors);

    return Object.keys(tempErrors).length === 0;
  };

  const handleAddPatient = async () => {
    if (!validateFields()) {
      setPopupMessage('Please correct the errors before submitting.');
      setTimeout(() => setPopupMessage(''), 3000);
      return;
    }

    try {
      const response = await axios.post('http://localhost:8080/api/patient/addPatient', newPatient);
      setPatients([...patients, response.data]);
      setNewPatient({
        firstName: '',
        middleName: '',
        lastName: '',
        dateOfBirth: '',
        gender: '',
        address: '',
        tckn: '',
        passportNumber: '',
        isSmsActive: false,
        isEmailActive: false,
        phoneNumbers: [],
        emailAddresses: []
      });
      setErrors({});
      setPopupMessage('Patient added successfully!');
      setTimeout(() => setPopupMessage(''), 3000);
    } catch (error) {
      console.error('Error adding patient:', error);
      setPopupMessage('An error occurred while adding the patient.');
      setTimeout(() => setPopupMessage(''), 3000);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:8080/api/patient/delete/${id}`);
      setPatients(patients.filter(patient => patient.id !== id));
      setPopupMessage('Patient deleted successfully!');
      setTimeout(() => setPopupMessage(''), 3000);
    } catch (error) {
      console.error('Error deleting patient:', error);
      setPopupMessage('An error occurred while deleting the patient.');
      setTimeout(() => setPopupMessage(''), 3000);
    }
  };

  const handleUpdatePatient = async (id) => {
    if (!validateFieldsForUpdate()) {
      setPopupMessage('Please correct the errors before submitting.');
      setTimeout(() => setPopupMessage(''), 3000);
      return;
    }
  
    try {
      const response = await axios.put(`http://localhost:8080/api/patient/update/${id}`, newPatient);
      setPatients(patients.map(patient => patient.id === id ? response.data : patient));
      setNewPatient({
        firstName: '',
        middleName: '',
        lastName: '',
        dateOfBirth: '',
        gender: '',
        address: '',
        tckn: '',
        passportNumber: '',
        isSmsActive: false,
        isEmailActive: false,
        phoneNumbers: [],
        emailAddresses: []
      });
      setErrors({});
      setPopupMessage('Patient updated successfully!');
      setTimeout(() => setPopupMessage(''), 3000);
    } catch (error) {
      console.error('Error updating patient:', error);
      setPopupMessage('An error occurred while updating the patient.');
      setTimeout(() => setPopupMessage(''), 3000);
    }
  };

  const searchPatientsByName = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/api/patient/searchName/${searchQuery}`);
      setPatients(response.data);
    } catch (error) {
      console.error('Error searching patients:', error);
    }
  };
  
  const addPhoneNumberField = () => {
    if (newPatient.phoneNumbers.length < 3) {
      setNewPatient(prevState => ({
        ...prevState,
        phoneNumbers: [...prevState.phoneNumbers, { phoneNumber: '', phoneType: 'mobile' }]
      }));
    }
  };

  const removePhoneNumberField = (index) => {
    setNewPatient(prevState => ({
      ...prevState,
      phoneNumbers: prevState.phoneNumbers.filter((_, i) => i !== index)
    }));
  };

  const addEmailAddressField = () => {
    if (newPatient.emailAddresses.length < 3) {
      setNewPatient(prevState => ({
        ...prevState,
        emailAddresses: [...prevState.emailAddresses, { emailAddress: '', emailType: 'Personal' }]
      }));
    }
  };

  const removeEmailAddressField = (index) => {
    setNewPatient(prevState => ({
      ...prevState,
      emailAddresses: prevState.emailAddresses.filter((_, i) => i !== index)
    }));
  };

  return (
    <div style={{ textAlign: 'center', margin: '0 auto', width: '80%' }}>
      {/* Popup Message */}
      {popupMessage && (
        <div style={{
          position: 'fixed',
          top: '10px',
          right: '10px',
          backgroundColor: '#f8d7da',
          color: '#721c24',
          border: '1px solid #f5c6cb',
          borderRadius: '5px',
          padding: '10px',
          zIndex: 1000
        }}>
          {popupMessage}
        </div>
      )}

      <Link to="/notifications" style={{ float: 'right', fontSize: '18px', marginRight: '20px', textDecoration: 'none', color: '#007bff' }}>
        Go to Notification Page
      </Link>

      <h1 style={{ marginBottom: '20px', fontSize: '36px' }}>Hospital Patient Management</h1>

      {/* Add Patient Form */}
      <div style={{ marginBottom: '40px', padding: '20px', border: '1px solid #ccc', borderRadius: '10px', backgroundColor: '#f9f9f9' }}>
        <h2 style={{ marginBottom: '20px' }}>Add New Patient</h2>
        <form>
          <input type="text" name="firstName" placeholder="First Name" value={newPatient.firstName} onChange={handleInputChange} style={{ margin: '10px', padding: '10px', borderRadius: '5px', border: '1px solid #ccc' }} />
          {errors.firstName && <div style={{ color: 'red' }}>{errors.firstName}</div>}

          <input type="text" name="middleName" placeholder="Middle Name" value={newPatient.middleName} onChange={handleInputChange} style={{ margin: '10px', padding: '10px', borderRadius: '5px', border: '1px solid #ccc' }} />

          <input type="text" name="lastName" placeholder="Last Name" value={newPatient.lastName} onChange={handleInputChange} style={{ margin: '10px', padding: '10px', borderRadius: '5px', border: '1px solid #ccc' }} />
          {errors.lastName && <div style={{ color: 'red' }}>{errors.lastName}</div>}

          <input type="date" name="dateOfBirth" placeholder="Date of Birth" value={newPatient.dateOfBirth} onChange={handleInputChange} style={{ margin: '10px', padding: '10px', borderRadius: '5px', border: '1px solid #ccc' }} />
          {errors.dateOfBirth && <div style={{ color: 'red' }}>{errors.dateOfBirth}</div>}

          <input type="text" name="gender" placeholder="Gender" value={newPatient.gender} onChange={handleInputChange} style={{ margin: '10px', padding: '10px', borderRadius: '5px', border: '1px solid #ccc' }} />
          {errors.gender && <div style={{ color: 'red' }}>{errors.gender}</div>}

          <input type="text" name="address" placeholder="Address" value={newPatient.address} onChange={handleInputChange} style={{ margin: '10px', padding: '10px', borderRadius: '5px', border: '1px solid #ccc' }} />
          {errors.address && <div style={{ color: 'red' }}>{errors.address}</div>}

          <input type="text" name="tckn" placeholder="TCKN" value={newPatient.tckn} onChange={handleInputChange} style={{ margin: '10px', padding: '10px', borderRadius: '5px', border: '1px solid #ccc' }} />
          {errors.tckn && <div style={{ color: 'red' }}>{errors.tckn}</div>}

          <input type="text" name="passportNumber" placeholder="Passport Number" value={newPatient.passportNumber} onChange={handleInputChange} style={{ margin: '10px', padding: '10px', borderRadius: '5px', border: '1px solid #ccc' }} />
          {errors.passportNumber && <div style={{ color: 'red' }}>{errors.passportNumber}</div>}

          <div>
            <label>
              <input type="checkbox" name="isSmsActive" checked={newPatient.isSmsActive} onChange={handleCheckboxChange} />
              SMS Active
            </label>
            <label>
              <input type="checkbox" name="isEmailActive" checked={newPatient.isEmailActive} onChange={handleCheckboxChange} />
              Email Active
            </label>
          </div>

          <div style={{ marginTop: '20px' }}>
            <h3>Phone Numbers</h3>
            {newPatient.phoneNumbers.map((phone, index) => (
              <div key={index} style={{ marginBottom: '10px' }}>
                <input type="text" name="phoneNumber" placeholder="Phone Number" value={phone.phoneNumber} onChange={(e) => handlePhoneNumberChange(index, e)} style={{ marginRight: '10px' }} />
                <input type="text" name="phoneType" placeholder="Phone Type" value={phone.phoneType} onChange={(e) => handlePhoneNumberChange(index, e)} />
                {index > 0 && (
                  <button type="button" onClick={() => removePhoneNumberField(index)} style={{ marginLeft: '10px', backgroundColor: '#dc3545', color: 'white', border: 'none', borderRadius: '5px', padding: '5px 10px' }}>Remove</button>
                )}
              </div>
            ))}
            <button type="button" onClick={addPhoneNumberField} style={{ backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '5px', padding: '5px 10px' }}>Add Phone Number</button>
          </div>

          <div style={{ marginTop: '20px' }}>
            <h3>Email Addresses</h3>
            {newPatient.emailAddresses.map((email, index) => (
              <div key={index} style={{ marginBottom: '10px' }}>
                <input type="text" name="emailAddress" placeholder="Email Address" value={email.emailAddress} onChange={(e) => handleEmailAddressChange(index, e)} style={{ marginRight: '10px' }} />
                <input type="text" name="emailType" placeholder="Email Type" value={email.emailType} onChange={(e) => handleEmailAddressChange(index, e)} />
                {index > 0 && (
                  <button type="button" onClick={() => removeEmailAddressField(index)} style={{ marginLeft: '10px', backgroundColor: '#dc3545', color: 'white', border: 'none', borderRadius: '5px', padding: '5px 10px' }}>Remove</button>
                )}
              </div>
            ))}
            <button type="button" onClick={addEmailAddressField} style={{ backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '5px', padding: '5px 10px' }}>Add Email Address</button>
          </div>

          <button type="button" onClick={handleAddPatient} style={{ marginTop: '20px', padding: '10px 20px', borderRadius: '5px', backgroundColor: '#28a745', color: 'white', border: 'none' }}>Add Patient</button>
        </form>
      </div>

      {/* Search */}
      <div style={{ marginBottom: '20px' }}>
        <input 
          type="text" 
          value={searchQuery} 
          onChange={(e) => setSearchQuery(e.target.value)} 
          placeholder="Search by name" 
          style={{ padding: '10px', borderRadius: '5px', border: '1px solid #ccc' }} 
        />
        <button onClick={searchPatientsByName} style={{ marginLeft: '10px', padding: '10px 20px', borderRadius: '5px', backgroundColor: '#007bff', color: 'white', border: 'none' }}>Search</button>
      </div>

      {/* Patients Table */}
      <table style={{
        width: '100%',
        borderCollapse: 'collapse',
        textAlign: 'center',
        marginBottom: '20px',
        boxShadow: '0px 0px 15px rgba(0, 0, 0, 0.1)',
        borderRadius: '10px',
        overflow: 'hidden'
      }}>
        <thead>
          <tr style={{ backgroundColor: '#007bff', color: 'white' }}>
            <th style={{ padding: '15px', borderRight: '1px solid white' }}>ID</th>
            <th style={{ padding: '15px', borderRight: '1px solid white' }}>First Name</th>
            <th style={{ padding: '15px', borderRight: '1px solid white' }}>Middle Name</th>
            <th style={{ padding: '15px', borderRight: '1px solid white' }}>Last Name</th>
            <th style={{ padding: '15px', borderRight: '1px solid white' }}>Date of Birth</th>
            <th style={{ padding: '15px', borderRight: '1px solid white' }}>Gender</th>
            <th style={{ padding: '15px', borderRight: '1px solid white' }}>Address</th>
            <th style={{ padding: '15px', borderRight: '1px solid white' }}>TCKN</th>
            <th style={{ padding: '15px', borderRight: '1px solid white' }}>Passport Number</th>
            <th style={{ padding: '15px', borderRight: '1px solid white' }}>Phone Numbers</th>
            <th style={{ padding: '15px', borderRight: '1px solid white' }}>Email Addresses</th>
            <th style={{ padding: '15px', borderRight: '1px solid white' }}>SMS Active</th>
            <th style={{ padding: '15px', borderRight: '1px solid white' }}>Email Active</th>
            <th style={{ padding: '15px' }}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {patients.map(patient => (
            <tr key={patient.id} style={{
              backgroundColor: '#f9f9f9',
              borderBottom: '1px solid #ddd'
            }}>
              <td style={{ padding: '10px', borderRight: '1px solid #ddd' }}>{patient.id}</td>
              <td style={{ padding: '10px', borderRight: '1px solid #ddd' }}>{patient.firstName}</td>
              <td style={{ padding: '10px', borderRight: '1px solid #ddd' }}>{patient.middleName}</td>
              <td style={{ padding: '10px', borderRight: '1px solid #ddd' }}>{patient.lastName}</td>
              <td style={{ padding: '10px', borderRight: '1px solid #ddd' }}>{new Date(patient.dateOfBirth).toLocaleDateString()}</td>
              <td style={{ padding: '10px', borderRight: '1px solid #ddd' }}>{patient.gender}</td>
              <td style={{ padding: '10px', borderRight: '1px solid #ddd' }}>{patient.address}</td>
              <td style={{ padding: '10px', borderRight: '1px solid #ddd' }}>{patient.tckn}</td>
              <td style={{ padding: '10px', borderRight: '1px solid #ddd' }}>{patient.passportNumber}</td>
              <td style={{ padding: '10px', borderRight: '1px solid #ddd' }}>
                {patient.phoneNumbers.map((phone, index) => (
                  <div key={index}>
                    {phone.phoneNumber} ({phone.phoneType})
                  </div>
                ))}
              </td>
              <td style={{ padding: '10px', borderRight: '1px solid #ddd' }}>
                {patient.emailAddresses.map((email, index) => (
                  <div key={index}>
                    {email.emailAddress} ({email.emailType})
                  </div>
                ))}
              </td>
              <td style={{ padding: '10px', borderRight: '1px solid #ddd' }}>{patient.isSmsActive ? 'Yes' : 'No'}</td>
              <td style={{ padding: '10px', borderRight: '1px solid #ddd' }}>{patient.isEmailActive ? 'Yes' : 'No'}</td>
              <td style={{ padding: '10px' }}>
                <button onClick={() => handleUpdatePatient(patient.id)} style={{ marginRight: '10px', backgroundColor: '#ffc107', color: 'white', border: 'none', borderRadius: '5px', padding: '5px 10px' }}>Update</button>
                <button onClick={() => handleDelete(patient.id)} style={{ backgroundColor: '#dc3545', color: 'white', border: 'none', borderRadius: '5px', padding: '5px 10px' }}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default HospitalPage;
