import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom'; // React Router'dan Link bileşeni

const HospitalPage = () => {
  const [patients, setPatients] = useState([]);
  const [newPatient, setNewPatient] = useState({
    firstName: '',
    middleName: '',
    lastName: '',
    dateOfBirth: '',
    gender: '',
    address: '',
    tckn: '',
    passportNumber: '',
    isSmsActive: false,
    isEmailActive: false,
    phoneNumbers: [],
    emailAddresses: []
  });
  const [errors, setErrors] = useState({});
  const [popupMessage, setPopupMessage] = useState('');

  useEffect(() => {
    fetchPatients();
  }, []);

  const fetchPatients = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/patient/getall');
      setPatients(response.data);
      console.log(response.data);
    } catch (error) {
      console.error('Error fetching patients:', error);
    }
  };

//   const handleInputChange = (e) => {
//     const { name, value } = e.target;
    
//     // TCKN alanında sadece sayıların girilmesine izin ver
//     if (name === 'tckn' && isNaN(value)) {
//       return;
//     }

//     setNewPatient(prevState => ({ ...prevState, [name]: value }));
//   };

//   const handleCheckboxChange = (e) => {
//     const { name, checked } = e.target;
//     setNewPatient(prevState => ({ ...prevState, [name]: checked }));
//   };
const handleInputChange = (e) => {
    const { name, value } = e.target;
    const [field, index, subField] = name.split('.');

    if (field === 'phoneNumbers' || field === 'emailAddresses') {
        setNewPatient(prevState => {
            const updatedArray = [...prevState[field]];
            updatedArray[index][subField] = value;
            return { ...prevState, [field]: updatedArray };
        });
    } else {
        // TCKN alanında sadece sayıların girilmesine izin ver
        if (name === 'tckn' && isNaN(value)) {
            return;
        }
        setNewPatient(prevState => ({ ...prevState, [name]: value }));
    }
};


  const validateFields = () => {
    let tempErrors = {};
    if (!newPatient.firstName) tempErrors.firstName = 'First Name is required';
    if (!newPatient.lastName) tempErrors.lastName = 'Last Name is required';
    if (!newPatient.dateOfBirth) tempErrors.dateOfBirth = 'Date of Birth is required';
    if (!newPatient.gender || newPatient.gender.length !== 1) tempErrors.gender = 'Gender must be 1 character';
    if (!newPatient.address) tempErrors.address = 'Address is required';
    if (!newPatient.tckn || newPatient.tckn.length !== 11) tempErrors.tckn = 'TCKN must be 11 digits';
    if (!newPatient.passportNumber) tempErrors.passportNumber = 'Passport Number is required';
    setErrors(tempErrors);
  
    return Object.keys(tempErrors).length === 0;
  };

  const handleAddPatient = async () => {
    if (!validateFields()) {
      setPopupMessage('Please correct the errors before submitting.');
      setTimeout(() => setPopupMessage(''), 3000); // Popup mesajı 3 saniye sonra kaybolur
      return;
    }

    try {
      const response = await axios.post('http://localhost:8080/api/patient/addPatient', newPatient);
      setPatients([...patients, response.data]);
      setNewPatient({
        firstName: '',
        middleName: '',
        lastName: '',
        dateOfBirth: '',
        gender: '',
        address: '',
        tckn: '',
        passportNumber: '',
        isSmsActive: false,
        isEmailActive: false,
        phoneNumbers: [],
        emailAddresses: []
      });
      setErrors({});
      setPopupMessage('Patient added successfully!');
      setTimeout(() => setPopupMessage(''), 3000); // Popup mesajı 3 saniye sonra kaybolur
    } catch (error) {
      console.error('Error adding patient:', error);
      setPopupMessage('An error occurred while adding the patient.');
      setTimeout(() => setPopupMessage(''), 3000); // Popup mesajı 3 saniye sonra kaybolur
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:8080/api/patient/delete/${id}`);
      setPatients(patients.filter(patient => patient.id !== id));
      setPopupMessage('Patient deleted successfully!');
      setTimeout(() => setPopupMessage(''), 3000); // Popup mesajı 3 saniye sonra kaybolur
    } catch (error) {
      console.error('Error deleting patient:', error);
      setPopupMessage('An error occurred while deleting the patient.');
      setTimeout(() => setPopupMessage(''), 3000); // Popup mesajı 3 saniye sonra kaybolur
    }
  };

  const addPhoneNumberField = () => {
    if (newPatient.phoneNumbers.length < 3) {
      setNewPatient(prevState => ({
        ...prevState,
        phoneNumbers: [...prevState.phoneNumbers, { phoneNumber: '', phoneType: 'mobile' }]
      }));
    }
  };

  const removePhoneNumberField = (index) => {
    setNewPatient(prevState => ({
      ...prevState,
      phoneNumbers: prevState.phoneNumbers.filter((_, i) => i !== index)
    }));
  };

  const addEmailAddressField = () => {
    if (newPatient.emailAddresses.length < 3) {
      setNewPatient(prevState => ({
        ...prevState,
        emailAddresses: [...prevState.emailAddresses, { emailAddress: '', emailType: 'Personal' }]
      }));
    }
  };

  const removeEmailAddressField = (index) => {
    setNewPatient(prevState => ({
      ...prevState,
      emailAddresses: prevState.emailAddresses.filter((_, i) => i !== index)
    }));
  };

  return (
    <div style={{ textAlign: 'center', margin: '0 auto', width: '80%' }}>
      {/* Popup Message */}
      {popupMessage && (
        <div style={{
          position: 'fixed',
          top: '10px',
          right: '10px',
          backgroundColor: '#f8d7da',
          color: '#721c24',
          border: '1px solid #f5c6cb',
          borderRadius: '5px',
          padding: '10px',
          zIndex: 1000
        }}>
          {popupMessage}
        </div>
      )}

      <Link to="/notifications" style={{ float: 'right', fontSize: '18px', marginRight: '20px' }}>
        Go to Notification Page
      </Link>

      <h1>Hospital Patient Management</h1>

      {/* Add Patient Form */}
      <div style={{ marginBottom: '20px' }}>
        <h2>Add New Patient</h2>
        <input
          type="text"
          name="id"
          placeholder="ID"
          value={newPatient.id}
          onChange={handleInputChange}
        //   style={{ borderColor: errors.firstName ? 'red' : '' }}
        />
        <input
          type="text"
          name="firstName"
          placeholder="First Name"
          value={newPatient.firstName}
          onChange={handleInputChange}
          style={{ borderColor: errors.firstName ? 'red' : '' }}
        />
        <input
          type="text"
          name="middleName"
          placeholder="Middle Name"
          value={newPatient.middleName}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="lastName"
          placeholder="Last Name"
          value={newPatient.lastName}
          onChange={handleInputChange}
          style={{ borderColor: errors.lastName ? 'red' : '' }}
        />
        <input
          type="date"
          name="dateOfBirth"
          placeholder="Date of Birth"
          value={newPatient.dateOfBirth}
          onChange={handleInputChange}
          style={{ borderColor: errors.dateOfBirth ? 'red' : '' }}
        />
        <input
          type="text"
          name="gender"
          placeholder="Gender"
          value={newPatient.gender}
          onChange={handleInputChange}
          style={{ borderColor: errors.gender ? 'red' : '' }}
        />
        <input
          type="text"
          name="address"
          placeholder="Address"
          value={newPatient.address}
          onChange={handleInputChange}
          style={{ borderColor: errors.address ? 'red' : '' }}
        />
        <input
          type="text"
          name="tckn"
          placeholder="TCKN"
          value={newPatient.tckn}
          onChange={handleInputChange}
          style={{ borderColor: errors.tckn ? 'red' : '' }}
        />
        <input
          type="text"
          name="passportNumber"
          placeholder="Passport Number"
          value={newPatient.passportNumber}
          onChange={handleInputChange}
          style={{ borderColor: errors.passportNumber ? 'red' : '' }}
        />

        <div style={{ marginBottom: '20px' }}>
          <label>
            <input
              type="checkbox"
              name="isSmsActive"
              checked={newPatient.isSmsActive}
              onChange={handleCheckboxChange}
            />
            SMS Notifications
          </label>
          <label>
            <input
              type="checkbox"
              name="isEmailActive"
              checked={newPatient.isEmailActive}
              onChange={handleCheckboxChange}
            />
            Email Notifications
          </label>
        </div>

        <div style={{ display: 'flex', flexDirection: 'row', flexWrap: 'wrap' }}>
          <div>
            <h3>Phone Numbers</h3>
            {newPatient.phoneNumbers.map((phone, index) => (
              <div key={index} style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
                <input
                  type="text"
                  name={`phoneNumbers[${index}].phoneNumber`}
                  placeholder="Phone Number"
                  value={phone.phoneNumber}
                  onChange={handleInputChange}
                  style={{ marginRight: '10px' }}
                />
                <select
                  name={`phoneNumbers[${index}].phoneType`}
                  value={phone.phoneType}
                  onChange={handleInputChange}
                  style={{ marginRight: '10px' }}
                >
                  <option value="mobile">Mobile</option>
                  <option value="home">Home</option>
                </select>
                <button
                  type="button"
                  onClick={() => removePhoneNumberField(index)}
                  style={{ marginLeft: '10px', backgroundColor: '#dc3545', color: '#fff', border: 'none', borderRadius: '5px', padding: '5px' }}
                >
                  Remove
                </button>
              </div>
            ))}
            {newPatient.phoneNumbers.length < 3 && (
              <button
                type="button"
                onClick={addPhoneNumberField}
                style={{ backgroundColor: '#007bff', color: '#fff', border: 'none', borderRadius: '5px', padding: '10px' }}
              >
                Add Phone Number
              </button>
            )}
          </div>

          <div style={{ marginLeft: '20px' }}>
            <h3>Email Addresses</h3>
            {newPatient.emailAddresses.map((email, index) => (
              <div key={index} style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
                <input
                  type="email"
                  name={`emailAddresses[${index}].emailAddress`}
                  placeholder="Email Address"
                  value={email.emailAddress}
                  onChange={handleInputChange}
                  style={{ marginRight: '10px' }}
                />
                <select
                  name={`emailAddresses[${index}].emailType`}
                  value={email.emailType}
                  onChange={handleInputChange}
                  style={{ marginRight: '10px' }}
                >
                  <option value="Personal">Personal</option>
                  <option value="Work">Work</option>
                </select>
                <button
                  type="button"
                  onClick={() => removeEmailAddressField(index)}
                  style={{ marginLeft: '10px', backgroundColor: '#dc3545', color: '#fff', border: 'none', borderRadius: '5px', padding: '5px' }}
                >
                  Remove
                </button>
              </div>
            ))}
            {newPatient.emailAddresses.length < 3 && (
              <button
                type="button"
                onClick={addEmailAddressField}
                style={{ backgroundColor: '#007bff', color: '#fff', border: 'none', borderRadius: '5px', padding: '10px' }}
              >
                Add Email Address
              </button>
            )}
          </div>
        </div>

        <button
          type="button"
          onClick={handleAddPatient}
          style={{ marginTop: '20px', backgroundColor: '#28a745', color: '#fff', border: 'none', borderRadius: '5px', padding: '10px 20px' }}
        >
          Add Patient
        </button>
      </div>

      {/* Patients Table */}
      <h2>Patients List</h2>
      <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '20px' }}>
        <thead>
          <tr>
          <th style={{ border: '1px solid #ddd', padding: '8px' }}>ID</th>
            <th style={{ border: '1px solid #ddd', padding: '8px' }}>First Name</th>
            <th style={{ border: '1px solid #ddd', padding: '8px' }}>Middle Name</th>
            <th style={{ border: '1px solid #ddd', padding: '8px' }}>Last Name</th>
            <th style={{ border: '1px solid #ddd', padding: '8px' }}>Date of Birth</th>
            <th style={{ border: '1px solid #ddd', padding: '8px' }}>Gender</th>
            <th style={{ border: '1px solid #ddd', padding: '8px' }}>Address</th>
            <th style={{ border: '1px solid #ddd', padding: '8px' }}>TCKN</th>
            <th style={{ border: '1px solid #ddd', padding: '8px' }}>Passport Number</th>
            <th style={{ border: '1px solid #ddd', padding: '8px' }}>Phone Numbers</th>
            <th style={{ border: '1px solid #ddd', padding: '8px' }}>Email Addresses</th>
            <th style={{ border: '1px solid #ddd', padding: '8px' }}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {patients.map(patient => (
            <tr key={patient.id}>
              <td style={{ border: '1px solid #ddd', padding: '8px' }}>{patient.id}</td>
              <td style={{ border: '1px solid #ddd', padding: '8px' }}>{patient.firstName}</td>
              <td style={{ border: '1px solid #ddd', padding: '8px' }}>{patient.middleName}</td>
              <td style={{ border: '1px solid #ddd', padding: '8px' }}>{patient.lastName}</td>
              <td style={{ border: '1px solid #ddd', padding: '8px' }}>{new Date(patient.dateOfBirth).toLocaleDateString('en-GB')}</td>
              <td style={{ border: '1px solid #ddd', padding: '8px' }}>{patient.gender}</td>
              <td style={{ border: '1px solid #ddd', padding: '8px' }}>{patient.address}</td>
              <td style={{ border: '1px solid #ddd', padding: '8px' }}>{patient.tckn}</td>
              <td style={{ border: '1px solid #ddd', padding: '8px' }}>{patient.passportNumber}</td>
              <td style={{ border: '1px solid #ddd', padding: '8px' }}>
                {patient.phoneNumbers.map((phone, index) => (
                  <div key={index}>{phone.phoneNumber} ({phone.phoneType})</div>
                ))}
              </td>
              <td style={{ border: '1px solid #ddd', padding: '8px' }}>
                {patient.emailAddresses.map((email, index) => (
                  <div key={index}>{email.emailAddress} ({email.emailType})</div>
                ))}
              </td>
              <td style={{ border: '1px solid #ddd', padding: '8px' }}>
                <button
                  onClick={() => handleDelete(patient.id)}
                  style={{ backgroundColor: '#dc3545', color: '#fff', border: 'none', borderRadius: '5px', padding: '5px' }}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default HospitalPage;
