import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const HospitalPage = () => {
  const [patients, setPatients] = useState([]);
  const [newPatient, setNewPatient] = useState({
    firstName: '',
    middleName: '',
    lastName: '',
    dateOfBirth: '',
    gender: '',
    address: '',
    tckn: '',
    passportNumber: '',
    isSmsActive: false,
    isEmailActive: false,
    phoneNumbers: '',
    emailAddresses: ''
  });

  const [errors, setErrors] = useState({});
  const [showErrorModal, setShowErrorModal] = useState(false);

  useEffect(() => {
    fetchPatients();
  }, []);

  const fetchPatients = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/patient/getall');
      setPatients(response.data);
      console.log(response.data);
    } catch (error) {
      console.error('Error fetching patients:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewPatient(prevState => ({ ...prevState, [name]: value }));
  };

  const handleCheckboxChange = (e) => {
    const { name, checked } = e.target;
    setNewPatient(prevState => ({ ...prevState, [name]: checked }));
  };

  const validateForm = () => {
    const newErrors = {};

    if (!newPatient.firstName) newErrors.firstName = 'First Name is required';
    if (!newPatient.lastName) newErrors.lastName = 'Last Name is required';
    if (!newPatient.dateOfBirth) newErrors.dateOfBirth = 'Date of Birth is required';
    if (!newPatient.gender || !/^[a-zA-Z]$/.test(newPatient.gender)) newErrors.gender = 'Gender must be a single letter';
    if (!newPatient.address) newErrors.address = 'Address is required';
    if (!newPatient.tckn || !/^\d{11}$/.test(newPatient.tckn)) newErrors.tckn = 'TCKN must be 11 digits';
    if (!newPatient.passportNumber) newErrors.passportNumber = 'Passport Number is required';

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleAddPatient = async () => {
    if (!validateForm()) {
      setShowErrorModal(true);
      return;
    }

    try {
      const phoneNumbersArray = newPatient.phoneNumbers
        ? newPatient.phoneNumbers.split(',').map(number => ({ phoneNumber: number.trim() }))
        : [];
      const emailAddressesArray = newPatient.emailAddresses
        ? newPatient.emailAddresses.split(',').map(email => ({ emailAddress: email.trim() }))
        : [];

      const patientToAdd = {
        ...newPatient,
        phoneNumbers: phoneNumbersArray,
        emailAddresses: emailAddressesArray
      };

      const response = await axios.post('http://localhost:8080/api/patient/addPatient', patientToAdd);
      setPatients([...patients, response.data]);
      setNewPatient({
        firstName: '',
        middleName: '',
        lastName: '',
        dateOfBirth: '',
        gender: '',
        address: '',
        tckn: '',
        passportNumber: '',
        isSmsActive: false,
        isEmailActive: false,
        phoneNumbers: '',
        emailAddresses: ''
      });
      setErrors({});
      setShowErrorModal(false);
    } catch (error) {
      console.error('Error adding patient:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:8080/api/patient/delete/${id}`);
      setPatients(patients.filter(patient => patient.id !== id));
    } catch (error) {
      console.error('Error deleting patient:', error);
    }
  };

  return (
    <div>
      <Link to="/notifications" style={{ float: 'right', fontSize: '18px', marginRight: '20px' }}>
        Go to Notification Page
      </Link>

      <h1>Hospital Patient Management</h1>

      {/* Add Patient Form */}
      <div style={{ marginBottom: '20px' }}>
        <h2>Add New Patient</h2>
        <input
          type="text"
          name="firstName"
          placeholder="First Name"
          value={newPatient.firstName}
          onChange={handleInputChange}
        />
        {errors.firstName && <div className="error">{errors.firstName}</div>}
        <input
          type="text"
          name="middleName"
          placeholder="Middle Name"
          value={newPatient.middleName}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="lastName"
          placeholder="Last Name"
          value={newPatient.lastName}
          onChange={handleInputChange}
        />
        {errors.lastName && <div className="error">{errors.lastName}</div>}
        <input
          type="date"
          name="dateOfBirth"
          placeholder="Date of Birth"
          value={newPatient.dateOfBirth}
          onChange={handleInputChange}
        />
        {errors.dateOfBirth && <div className="error">{errors.dateOfBirth}</div>}
        <input
          type="text"
          name="gender"
          placeholder="Gender"
          value={newPatient.gender}
          onChange={handleInputChange}
        />
        {errors.gender && <div className="error">{errors.gender}</div>}
        <input
          type="text"
          name="address"
          placeholder="Address"
          value={newPatient.address}
          onChange={handleInputChange}
        />
        {errors.address && <div className="error">{errors.address}</div>}
        <input
          type="text"
          name="tckn"
          placeholder="TCKN"
          value={newPatient.tckn}
          onChange={handleInputChange}
        />
        {errors.tckn && <div className="error">{errors.tckn}</div>}
        <input
          type="text"
          name="passportNumber"
          placeholder="Passport Number"
          value={newPatient.passportNumber}
          onChange={handleInputChange}
        />
        {errors.passportNumber && <div className="error">{errors.passportNumber}</div>}

        <div>
          <label>
            <input
              type="checkbox"
              name="isSmsActive"
              checked={newPatient.isSmsActive}
              onChange={handleCheckboxChange}
            />
            SMS Notifications
          </label>
          <label>
            <input
              type="checkbox"
              name="isEmailActive"
              checked={newPatient.isEmailActive}
              onChange={handleCheckboxChange}
            />
            Email Notifications
          </label>
        </div>

        <textarea
          name="phoneNumbers"
          placeholder="Phone Numbers (comma separated)"
          value={newPatient.phoneNumbers}
          onChange={handleInputChange}
        />
        <textarea
          name="emailAddresses"
          placeholder="Email Addresses (comma separated)"
          value={newPatient.emailAddresses}
          onChange={handleInputChange}
        />
        <button onClick={handleAddPatient}>Add Patient</button>
      </div>

      {/* Error Modal */}
      {showErrorModal && (
        <div className="modal-overlay" style={modalOverlayStyle}>
          <div className="modal" style={modalStyle}>
            <h2>Form Errors</h2>
            {Object.values(errors).map((error, index) => (
              <p key={index}>{error}</p>
            ))}
            <button onClick={() => setShowErrorModal(false)}>Close</button>
          </div>
        </div>
      )}

      {/* Patient Table */}
      <table border="1" style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th>ID</th>
            <th>First Name</th>
            <th>Middle Name</th>
            <th>Last Name</th>
            <th>Date of Birth</th>
            <th>Gender</th>
            <th>Address</th>
            <th>TCKN</th>
            <th>Passport Number</th>
            <th>Phone Numbers</th>
            <th>Email Addresses</th>
            <th>SMS Active</th>
            <th>Email Active</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {patients.map(patient => (
            <tr key={patient.id}>
              <td>{patient.id}</td>
              <td>{patient.firstName}</td>
              <td>{patient.middleName}</td>
              <td>{patient.lastName}</td>
              <td>{patient.dateOfBirth}</td>
              <td>{patient.gender}</td>
              <td>{patient.address}</td>
              <td>{patient.tckn}</td>
              <td>{patient.passportNumber}</td>
              <td>{patient.phoneNumbers.map(pn => pn.phoneNumber).join(', ')}</td>
              <td>{patient.emailAddresses.map(ea => ea.emailAddress).join(', ')}</td>
              <td>{patient.isSmsActive ? 'Yes' : 'No'}</td>
              <td>{patient.isEmailActive ? 'Yes' : 'No'}</td>
              <td>
                <button onClick={() => handleDelete(patient.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

// Modal overlay style
const modalOverlayStyle = {
  position: 'fixed',
  top: 0,
  left: 0,
  width: '100%',
  height: '100%',
  backgroundColor: 'rgba(0, 0, 0, 0.5)',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
};

// Modal content style
const modalStyle = {
  backgroundColor: '#fff',
  padding: '20px',
  borderRadius: '5px',
  boxShadow: '0 0 10px rgba(0, 0, 0, 0.2)',
};

export default HospitalPage;
