import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom'; // React Router'dan Link bileşeni

const HospitalPage = () => {
  const [patients, setPatients] = useState([]);
  const [newPatient, setNewPatient] = useState({
    firstName: '',
    middleName: '',
    lastName: '',
    dateOfBirth: '',
    gender: '',
    address: '',
    tckn: '',
    passportNumber: '',
    isSmsActive: false,
    isEmailActive: false,
    phoneNumbers: [],
    emailAddresses: []
  });

  const [errors, setErrors] = useState({});

  useEffect(() => {
    fetchPatients();
  }, []);

  const fetchPatients = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/patient/getall');
      setPatients(response.data);
      console.log(response.data);
    } catch (error) {
      console.error('Error fetching patients:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewPatient(prevState => ({ ...prevState, [name]: value }));
  };

  const handleCheckboxChange = (e) => {
    const { name, checked } = e.target;
    setNewPatient(prevState => ({ ...prevState, [name]: checked }));
  };

  const validateForm = () => {
    const newErrors = {};
    if (!newPatient.firstName.trim()) newErrors.firstName = 'First name is required';
    if (!newPatient.lastName.trim()) newErrors.lastName = 'Last name is required';
    if (!newPatient.dateOfBirth) newErrors.dateOfBirth = 'Date of birth is required';
    if (!/^[A-Za-z]$/.test(newPatient.gender)) newErrors.gender = 'Gender must be a single letter';
    if (!newPatient.address.trim()) newErrors.address = 'Address is required';
    if (!/^\d{1,11}$/.test(newPatient.tckn)) newErrors.tckn = 'TCKN must be a number and up to 11 digits';

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleAddPatient = async () => {
    if (!validateForm()) {
      return;
    }

    try {
      const phoneNumbersArray = newPatient.phoneNumbers
        ? newPatient.phoneNumbers.split(',').map(number => ({ phoneNumber: number.trim() }))
        : [];
      const emailAddressesArray = newPatient.emailAddresses
        ? newPatient.emailAddresses.split(',').map(email => ({ emailAddress: email.trim() }))
        : [];

      const patientToAdd = {
        ...newPatient,
        phoneNumbers: phoneNumbersArray,
        emailAddresses: emailAddressesArray
      };

      const response = await axios.post('http://localhost:8080/api/patient/addPatient', patientToAdd);
      setPatients([...patients, response.data]);
      setNewPatient({
        firstName: '',
        middleName: '',
        lastName: '',
        dateOfBirth: '',
        gender: '',
        address: '',
        tckn: '',
        passportNumber: '',
        isSmsActive: false,
        isEmailActive: false,
        phoneNumbers: '',
        emailAddresses: ''
      });
      setErrors({});
    } catch (error) {
      console.error('Error adding patient:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:8080/api/patient/delete/${id}`);
      setPatients(patients.filter(patient => patient.id !== id));
    } catch (error) {
      console.error('Error deleting patient:', error);
    }
  };

  return (
    <div>
      <Link to="/notifications" style={{ float: 'right', fontSize: '18px', marginRight: '20px' }}>
        Go to Notification Page
      </Link>

      <h1>Hospital Patient Management</h1>

      {/* Add Patient Form */}
      <div style={{ marginBottom: '20px' }}>
        <h2>Add New Patient</h2>
        <input
          type="text"
          name="firstName"
          placeholder="First Name"
          value={newPatient.firstName}
          onChange={handleInputChange}
        />
        {errors.firstName && <p style={{ color: 'red' }}>{errors.firstName}</p>}
        
        <input
          type="text"
          name="middleName"
          placeholder="Middle Name"
          value={newPatient.middleName}
          onChange={handleInputChange}
        />
        
        <input
          type="text"
          name="lastName"
          placeholder="Last Name"
          value={newPatient.lastName}
          onChange={handleInputChange}
        />
        {errors.lastName && <p style={{ color: 'red' }}>{errors.lastName}</p>}
        
        <input
          type="date"
          name="dateOfBirth"
          placeholder="Date of Birth"
          value={newPatient.dateOfBirth}
          onChange={handleInputChange}
        />
        {errors.dateOfBirth && <p style={{ color: 'red' }}>{errors.dateOfBirth}</p>}
        
        <input
          type="text"
          name="gender"
          placeholder="Gender"
          value={newPatient.gender}
          onChange={handleInputChange}
        />
        {errors.gender && <p style={{ color: 'red' }}>{errors.gender}</p>}
        
        <input
          type="text"
          name="address"
          placeholder="Address"
          value={newPatient.address}
          onChange={handleInputChange}
        />
        {errors.address && <p style={{ color: 'red' }}>{errors.address}</p>}
        
        <input
          type="text"
          name="tckn"
          placeholder="TCKN"
          value={newPatient.tckn}
          onChange={handleInputChange}
        />
        {errors.tckn && <p style={{ color: 'red' }}>{errors.tckn}</p>}
        
        <input
          type="text"
          name="passportNumber"
          placeholder="Passport Number"
          value={newPatient.passportNumber}
          onChange={handleInputChange}
        />

        <div>
          <label>
            <input
              type="checkbox"
              name="isSmsActive"
              checked={newPatient.isSmsActive}
              onChange={handleCheckboxChange}
            />
            SMS Notifications
          </label>
          <label>
            <input
              type="checkbox"
              name="isEmailActive"
              checked={newPatient.isEmailActive}
              onChange={handleCheckboxChange}
            />
            Email Notifications
          </label>
        </div>

        <textarea
          name="phoneNumbers"
          placeholder="Phone Numbers (comma separated)"
          value={newPatient.phoneNumbers}
          onChange={handleInputChange}
        />
        <textarea
          name="emailAddresses"
          placeholder="Email Addresses (comma separated)"
          value={newPatient.emailAddresses}
          onChange={handleInputChange}
        />
        <button onClick={handleAddPatient}>Add Patient</button>
      </div>

      {/* Patient Table */}
      <table border="1" style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th>ID</th>
            <th>First Name</th>
            <th>Middle Name</th>
            <th>Last Name</th>
            <th>Date of Birth</th>
            <th>Gender</th>
            <th>Address</th>
            <th>TCKN</th>
            <th>Passport Number</th>
            <th>Phone Numbers</th>
            <th>Email Addresses</th>
            <th>SMS Active</th>
            <th>Email Active</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {patients.map(patient => (
            <tr key={patient.id}>
              <td>{patient.id}</td>
              <td>{patient.firstName}</td>
              <td>{patient.middleName}</td>
              <td>{patient.lastName}</td>
              <td>{patient.dateOfBirth}</td>
              <td>{patient.gender}</td>
              <td>{patient.address}</td>
              <td>{patient.tckn}</td>
              <td>{patient.passportNumber}</td>
              <td>{patient.phoneNumbers.map(pn => pn.phoneNumber).join(', ')}</td>
              <td>{patient.emailAddresses.map(ea => ea.emailAddress).join(', ')}</td>
              <td>{patient.isSmsActive ? 'Yes' : 'No'}</td>
              <td>{patient.isEmailActive ? 'Yes' : 'No'}</td>
              <td>
                <button onClick={() => handleDelete(patient.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default HospitalPage;
