import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom'; // React Router'dan Link bileÅŸeni

const HospitalPage = () => {
  const [patients, setPatients] = useState([]);
  const [newPatient, setNewPatient] = useState({
    firstName: '',
    middleName: '',
    lastName: '',
    dateOfBirth: '',
    gender: '',
    address: '',
    tckn: '',
    passportNumber: '',
    isSmsActive: false,
    isEmailActive: false,
    phoneNumbers: [{ phoneNumber: '', phoneType: 'mobile' }],
    emailAddresses: [{ emailAddress: '', emailType: 'Personal' }]
  });
  const [errors, setErrors] = useState({});
  const [popupMessage, setPopupMessage] = useState('');

  useEffect(() => {
    fetchPatients();
  }, []);

  const fetchPatients = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/patient/getall');
      setPatients(response.data);
      console.log(response.data);
    } catch (error) {
      console.error('Error fetching patients:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;

    if (name === 'tckn' && isNaN(value)) {
      return;
    }

    setNewPatient(prevState => ({ ...prevState, [name]: value }));
  };

  const handleCheckboxChange = (e) => {
    const { name, checked } = e.target;
    setNewPatient(prevState => ({ ...prevState, [name]: checked }));
  };

  const handlePhoneChange = (index, e) => {
    const { name, value } = e.target;
    const updatedPhoneNumbers = [...newPatient.phoneNumbers];
    updatedPhoneNumbers[index] = { ...updatedPhoneNumbers[index], [name]: value };
    setNewPatient({ ...newPatient, phoneNumbers: updatedPhoneNumbers });
  };

  const handleEmailChange = (index, e) => {
    const { name, value } = e.target;
    const updatedEmailAddresses = [...newPatient.emailAddresses];
    updatedEmailAddresses[index] = { ...updatedEmailAddresses[index], [name]: value };
    setNewPatient({ ...newPatient, emailAddresses: updatedEmailAddresses });
  };

  const addPhoneNumberField = () => {
    if (newPatient.phoneNumbers.length < 3) {
      setNewPatient(prevState => ({
        ...prevState,
        phoneNumbers: [...prevState.phoneNumbers, { phoneNumber: '', phoneType: 'mobile' }]
      }));
    }
  };

  const addEmailAddressField = () => {
    if (newPatient.emailAddresses.length < 3) {
      setNewPatient(prevState => ({
        ...prevState,
        emailAddresses: [...prevState.emailAddresses, { emailAddress: '', emailType: 'Personal' }]
      }));
    }
  };

  const validateFields = () => {
    let tempErrors = {};
    if (!newPatient.firstName) tempErrors.firstName = 'First Name is required';
    if (!newPatient.lastName) tempErrors.lastName = 'Last Name is required';
    if (!newPatient.dateOfBirth) tempErrors.dateOfBirth = 'Date of Birth is required';
    if (!newPatient.gender || newPatient.gender.length !== 1) tempErrors.gender = 'Gender must be 1 character';
    if (!newPatient.address) tempErrors.address = 'Address is required';
    if (!newPatient.tckn || newPatient.tckn.length !== 11) tempErrors.tckn = 'TCKN must be 11 digits';
    if (!newPatient.passportNumber) tempErrors.passportNumber = 'Passport Number is required';
    setErrors(tempErrors);
  
    return Object.keys(tempErrors).length === 0;
  };

  const handleAddPatient = async () => {
    if (!validateFields()) {
      setPopupMessage('Please correct the errors before submitting.');
      setTimeout(() => setPopupMessage(''), 3000);
      return;
    }

    try {
      const response = await axios.post('http://localhost:8080/api/patient/addPatient', newPatient);
      setPatients([...patients, response.data]);
      setNewPatient({
        firstName: '',
        middleName: '',
        lastName: '',
        dateOfBirth: '',
        gender: '',
        address: '',
        tckn: '',
        passportNumber: '',
        isSmsActive: false,
        isEmailActive: false,
        phoneNumbers: [{ phoneNumber: '', phoneType: 'mobile' }],
        emailAddresses: [{ emailAddress: '', emailType: 'Personal' }]
      });
      setErrors({});
      setPopupMessage('Patient added successfully!');
      setTimeout(() => setPopupMessage(''), 3000);
    } catch (error) {
      console.error('Error adding patient:', error);
      setPopupMessage('An error occurred while adding the patient.');
      setTimeout(() => setPopupMessage(''), 3000);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:8080/api/patient/delete/${id}`);
      setPatients(patients.filter(patient => patient.id !== id));
      setPopupMessage('Patient deleted successfully!');
      setTimeout(() => setPopupMessage(''), 3000);
    } catch (error) {
      console.error('Error deleting patient:', error);
      setPopupMessage('An error occurred while deleting the patient.');
      setTimeout(() => setPopupMessage(''), 3000);
    }
  };

  return (
    <div>
      {/* Popup Message */}
      {popupMessage && (
        <div style={{
          position: 'fixed',
          top: '10px',
          right: '10px',
          backgroundColor: '#f8d7da',
          color: '#721c24',
          border: '1px solid #f5c6cb',
          borderRadius: '5px',
          padding: '10px',
          zIndex: 1000
        }}>
          {popupMessage}
        </div>
      )}

      <Link to="/notifications" style={{ float: 'right', fontSize: '18px', marginRight: '20px' }}>
        Go to Notification Page
      </Link>

      <h1>Hospital Patient Management</h1>

      {/* Add Patient Form */}
      <div style={{ marginBottom: '20px' }}>
        <h2>Add New Patient</h2>
        <input
          type="text"
          name="firstName"
          placeholder="First Name"
          value={newPatient.firstName}
          onChange={handleInputChange}
          style={{ borderColor: errors.firstName ? 'red' : '' }}
        />
        <input
          type="text"
          name="middleName"
          placeholder="Middle Name"
          value={newPatient.middleName}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="lastName"
          placeholder="Last Name"
          value={newPatient.lastName}
          onChange={handleInputChange}
          style={{ borderColor: errors.lastName ? 'red' : '' }}
        />
        <input
          type="date"
          name="dateOfBirth"
          placeholder="Date of Birth"
          value={newPatient.dateOfBirth}
          onChange={handleInputChange}
          style={{ borderColor: errors.dateOfBirth ? 'red' : '' }}
        />
        <input
          type="text"
          name="gender"
          placeholder="Gender"
          value={newPatient.gender}
          onChange={handleInputChange}
          style={{ borderColor: errors.gender ? 'red' : '' }}
        />
        <input
          type="text"
          name="address"
          placeholder="Address"
          value={newPatient.address}
          onChange={handleInputChange}
          style={{ borderColor: errors.address ? 'red' : '' }}
        />
        <input
          type="text"
          name="tckn"
          placeholder="TCKN"
          value={newPatient.tckn}
          onChange={handleInputChange}
          style={{ borderColor: errors.tckn ? 'red' : '' }}
        />
        <input
          type="text"
          name="passportNumber"
          placeholder="Passport Number"
          value={newPatient.passportNumber}
          onChange={handleInputChange}
          style={{ borderColor: errors.passportNumber ? 'red' : '' }}
        />

        {/* Phone Numbers */}
        <div>
          <h3>Phone Numbers</h3>
          {newPatient.phoneNumbers.map((phone, index) => (
            <div key={index} style={{ display: 'flex', marginBottom: '10px' }}>
              <input
                type="text"
                name="phoneNumber"
                placeholder="Phone Number"
                value={phone.phoneNumber}
                onChange={(e) => handlePhoneChange(index, e)}
                style={{ borderColor: errors.phoneNumber ? 'red' : '', marginRight: '10px' }}
              />
              <select
                name="phoneType"
                value={phone.phoneType}
                onChange={(e) => handlePhoneChange(index, e)}
                style={{ marginRight: '10px' }}
              >
                <option value="mobile">Mobile</option>
                <option value="home">Home</option>
                <option value="work">Work</option>
              </select>
              {index === newPatient.phoneNumbers.length - 1 && newPatient.phoneNumbers.length < 3 && (
                <button type="button" onClick={addPhoneNumberField}>Add Phone Number</button>
              )}
            </div>
          ))}
        </div>

        {/* Email Addresses */}
        <div>
          <h3>Email Addresses</h3>
          {newPatient.emailAddresses.map((email, index) => (
            <div key={index} style={{ display: 'flex', marginBottom: '10px' }}>
              <input
                type="email"
                name="emailAddress"
                placeholder="Email Address"
                value={email.emailAddress}
                onChange={(e) => handleEmailChange(index, e)}
                style={{ borderColor: errors.emailAddress ? 'red' : '', marginRight: '10px' }}
              />
              <select
                name="emailType"
                value={email.emailType}
                onChange={(e) => handleEmailChange(index, e)}
                style={{ marginRight: '10px' }}
              >
                <option value="Personal">Personal</option>
                <option value="Work">Work</option>
                <option value="Other">Other</option>
              </select>
              {index === newPatient.emailAddresses.length - 1 && newPatient.emailAddresses.length < 3 && (
                <button type="button" onClick={addEmailAddressField}>Add Email Address</button>
              )}
            </div>
          ))}
        </div>

        {/* Add Patient Button */}
        <button onClick={handleAddPatient}>Add Patient</button>
      </div>

      {/* Patient List Table */}
      <h2>Patient List</h2>
      <table border="1" style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th>First Name</th>
            <th>Middle Name</th>
            <th>Last Name</th>
            <th>Date of Birth</th>
            <th>Gender</th>
            <th>Address</th>
            <th>TCKN</th>
            <th>Passport Number</th>
            <th>Phone Numbers</th>
            <th>Email Addresses</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {patients.map(patient => (
            <tr key={patient.id}>
              <td>{patient.firstName}</td>
              <td>{patient.middleName}</td>
              <td>{patient.lastName}</td>
              <td>{patient.dateOfBirth}</td>
              <td>{patient.gender}</td>
              <td>{patient.address}</td>
              <td>{patient.tckn}</td>
              <td>{patient.passportNumber}</td>
              <td>
                {patient.phoneNumbers.map((phone, idx) => (
                  <div key={idx}>{phone.phoneNumber} ({phone.phoneType})</div>
                ))}
              </td>
              <td>
                {patient.emailAddresses.map((email, idx) => (
                  <div key={idx}>{email.emailAddress} ({email.emailType})</div>
                ))}
              </td>
              <td>
                <button onClick={() => handleDelete(patient.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default HospitalPage;
