import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const HospitalPage = () => {
  const [patients, setPatients] = useState([]);
  const [searchName, setSearchName] = useState('');
  const [newPatient, setNewPatient] = useState({
    firstName: '',
    middleName: '',
    lastName: '',
    dateOfBirth: '',
    gender: '',
    address: '',
    tckn: '',
    passportNumber: '',
    isSmsActive: false,
    isEmailActive: false,
    phoneNumbers: [],
    emailAddresses: []
  });
  const [errors, setErrors] = useState({});
  const [popupMessage, setPopupMessage] = useState('');

  useEffect(() => {
    fetchPatients();
  }, []);

  const fetchPatients = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/patient/getall');
      setPatients(response.data);
    } catch (error) {
      console.error('Error fetching patients:', error);
    }
  };

  const searchPatientsByName = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/api/patient/searchName/${searchName}`);
      setPatients(response.data);
    } catch (error) {
      console.error('Error searching patients:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name === 'tckn' && isNaN(value)) return;
    setNewPatient(prevState => ({ ...prevState, [name]: value }));
  };

  const handlePhoneNumberChange = (index, e) => {
    const { name, value } = e.target;
    const updatedPhoneNumbers = [...newPatient.phoneNumbers];
    updatedPhoneNumbers[index][name] = value;
    setNewPatient(prevState => ({ ...prevState, phoneNumbers: updatedPhoneNumbers }));
  };

  const handleEmailAddressChange = (index, e) => {
    const { name, value } = e.target;
    const updatedEmailAddresses = [...newPatient.emailAddresses];
    updatedEmailAddresses[index][name] = value;
    setNewPatient(prevState => ({ ...prevState, emailAddresses: updatedEmailAddresses }));
  };

  const handleCheckboxChange = (e) => {
    const { name, checked } = e.target;
    setNewPatient(prevState => ({ ...prevState, [name]: checked }));
  };

  const validateFields = () => {
    let tempErrors = {};
    if (!newPatient.firstName) tempErrors.firstName = 'First Name is required';
    if (!newPatient.lastName) tempErrors.lastName = 'Last Name is required';
    if (!newPatient.dateOfBirth) tempErrors.dateOfBirth = 'Date of Birth is required';
    if (!newPatient.gender || newPatient.gender.length !== 1) tempErrors.gender = 'Gender must be 1 character';
    if (!newPatient.address) tempErrors.address = 'Address is required';
    if (!newPatient.tckn || newPatient.tckn.length !== 11) tempErrors.tckn = 'TCKN must be 11 digits';
    if (!newPatient.passportNumber) tempErrors.passportNumber = 'Passport Number is required';
    setErrors(tempErrors);

    return Object.keys(tempErrors).length === 0;
  };

  const handleAddPatient = async () => {
    if (!validateFields()) {
      setPopupMessage('Please correct the errors before submitting.');
      setTimeout(() => setPopupMessage(''), 3000);
      return;
    }

    try {
      const response = await axios.post('http://localhost:8080/api/patient/addPatient', newPatient);
      setPatients([...patients, response.data]);
      setNewPatient({
        firstName: '',
        middleName: '',
        lastName: '',
        dateOfBirth: '',
        gender: '',
        address: '',
        tckn: '',
        passportNumber: '',
        isSmsActive: false,
        isEmailActive: false,
        phoneNumbers: [],
        emailAddresses: []
      });
      setErrors({});
      setPopupMessage('Patient added successfully!');
      setTimeout(() => setPopupMessage(''), 3000);
    } catch (error) {
      console.error('Error adding patient:', error);
      setPopupMessage('An error occurred while adding the patient.');
      setTimeout(() => setPopupMessage(''), 3000);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:8080/api/patient/delete/${id}`);
      setPatients(patients.filter(patient => patient.id !== id));
      setPopupMessage('Patient deleted successfully!');
      setTimeout(() => setPopupMessage(''), 3000);
    } catch (error) {
      console.error('Error deleting patient:', error);
      setPopupMessage('An error occurred while deleting the patient.');
      setTimeout(() => setPopupMessage(''), 3000);
    }
  };

  const addPhoneNumberField = () => {
    if (newPatient.phoneNumbers.length < 3) {
      setNewPatient(prevState => ({
        ...prevState,
        phoneNumbers: [...prevState.phoneNumbers, { phoneNumber: '', phoneType: 'mobile' }]
      }));
    }
  };

  const removePhoneNumberField = (index) => {
    setNewPatient(prevState => ({
      ...prevState,
      phoneNumbers: prevState.phoneNumbers.filter((_, i) => i !== index)
    }));
  };

  const addEmailAddressField = () => {
    if (newPatient.emailAddresses.length < 3) {
      setNewPatient(prevState => ({
        ...prevState,
        emailAddresses: [...prevState.emailAddresses, { emailAddress: '', emailType: 'Personal' }]
      }));
    }
  };

  const removeEmailAddressField = (index) => {
    setNewPatient(prevState => ({
      ...prevState,
      emailAddresses: prevState.emailAddresses.filter((_, i) => i !== index)
    }));
  };

  const formatDateOfBirth = (dob) => {
    const date = new Date(dob);
    return `${('0' + date.getDate()).slice(-2)}/${('0' + (date.getMonth() + 1)).slice(-2)}/${date.getFullYear()}`;
  };

  return (
    <div style={{ textAlign: 'center', margin: '0 auto', width: '80%' }}>
      {/* Popup Message */}
      {popupMessage && (
        <div style={{
          position: 'fixed',
          top: '10px',
          right: '10px',
          backgroundColor: '#f8d7da',
          color: '#721c24',
          border: '1px solid #f5c6cb',
          borderRadius: '5px',
          padding: '10px',
          zIndex: 1000
        }}>
          {popupMessage}
        </div>
      )}

      <Link to="/notifications" style={{ float: 'right', fontSize: '18px', marginRight: '20px', textDecoration: 'none', color: '#007bff' }}>
        Go to Notification Page
      </Link>

      <h1 style={{ marginBottom: '20px', fontSize: '36px' }}>Hospital Patient Management</h1>

      {/* Search Box */}
      <div style={{ marginBottom: '20px' }}>
        <input
          type="text"
          placeholder="Search by name"
          value={searchName}
          onChange={(e) => setSearchName(e.target.value)}
          style={{ padding: '10px', borderRadius: '5px', width: '300px' }}
        />
        <button onClick={searchPatientsByName} style={{ padding: '10px 20px', borderRadius: '5px', backgroundColor: '#007bff', color: 'white', border: 'none', marginLeft: '10px' }}>
          Search
        </button>
      </div>

      {/* Add Patient Form */}
      <div style={{ marginBottom: '40px', padding: '20px', border: '1px solid #ccc', borderRadius: '10px', backgroundColor: '#f9f9f9' }}>
        <h2 style={{ marginBottom: '20px' }}>Add New Patient</h2>
        <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center', gap: '10px' }}>
          <input
            type="text"
            name="firstName"
            placeholder="First Name"
            value={newPatient.firstName}
            onChange={handleInputChange}
            style={{ borderColor: errors.firstName ? 'red' : '#ccc', padding: '10px', borderRadius: '5px', width: '220px' }}
          />
          <input
            type="text"
            name="middleName"
            placeholder="Middle Name"
            value={newPatient.middleName}
            onChange={handleInputChange}
            style={{ padding: '10px', borderRadius: '5px', width: '220px' }}
          />
          <input
            type="text"
            name="lastName"
            placeholder="Last Name"
            value={newPatient.lastName}
            onChange={handleInputChange}
            style={{ borderColor: errors.lastName ? 'red' : '#ccc', padding: '10px', borderRadius: '5px', width: '220px' }}
          />
          <input
            type="date"
            name="dateOfBirth"
            placeholder="Date of Birth"
            value={newPatient.dateOfBirth}
            onChange={handleInputChange}
            style={{ borderColor: errors.dateOfBirth ? 'red' : '#ccc', padding: '10px', borderRadius: '5px', width: '220px' }}
          />
          <input
            type="text"
            name="gender"
            placeholder="Gender"
            value={newPatient.gender}
            onChange={handleInputChange}
            style={{ borderColor: errors.gender ? 'red' : '#ccc', padding: '10px', borderRadius: '5px', width: '220px' }}
          />
          <input
            type="text"
            name="address"
            placeholder="Address"
            value={newPatient.address}
            onChange={handleInputChange}
            style={{ borderColor: errors.address ? 'red' : '#ccc', padding: '10px', borderRadius: '5px', width: '220px' }}
          />
          <input
            type="text"
            name="tckn"
            placeholder="TCKN"
            value={newPatient.tckn}
            onChange={handleInputChange}
            style={{ borderColor: errors.tckn ? 'red' : '#ccc', padding: '10px', borderRadius: '5px', width: '220px' }}
          />
          <input
            type="text"
            name="passportNumber"
            placeholder="Passport Number"
            value={newPatient.passportNumber}
            onChange={handleInputChange}
            style={{ borderColor: errors.passportNumber ? 'red' : '#ccc', padding: '10px', borderRadius: '5px', width: '220px' }}
          />
          <div style={{ display: 'flex', alignItems: 'center', marginTop: '10px', gap: '10px' }}>
            <label>SMS Active</label>
            <input
              type="checkbox"
              name="isSmsActive"
              checked={newPatient.isSmsActive}
              onChange={handleCheckboxChange}
            />
            <label>Email Active</label>
            <input
              type="checkbox"
              name="isEmailActive"
              checked={newPatient.isEmailActive}
              onChange={handleCheckboxChange}
            />
          </div>
        </div>

        {/* Phone Numbers Section */}
        <div style={{ marginTop: '20px', textAlign: 'center' }}>
          <h3>Phone Numbers</h3>
          {newPatient.phoneNumbers.map((phoneNumber, index) => (
            <div key={index} style={{ display: 'flex', justifyContent: 'center', gap: '10px', marginBottom: '10px' }}>
              <input
                type="text"
                name="phoneNumber"
                placeholder="Phone Number"
                value={phoneNumber.phoneNumber}
                onChange={(e) => handlePhoneNumberChange(index, e)}
                style={{ padding: '10px', borderRadius: '5px', width: '300px' }}
              />
              <select
                name="phoneType"
                value={phoneNumber.phoneType}
                onChange={(e) => handlePhoneNumberChange(index, e)}
                style={{ padding: '10px', borderRadius: '5px', width: '150px' }}
              >
                <option value="mobile">Mobile</option>
                <option value="home">Home</option>
                <option value="work">Work</option>
              </select>
              <button
                onClick={() => removePhoneNumberField(index)}
                style={{ padding: '10px 20px', borderRadius: '5px', backgroundColor: '#dc3545', color: 'white', border: 'none' }}
              >
                Remove
              </button>
            </div>
          ))}
          <button onClick={addPhoneNumberField} style={{ padding: '10px 20px', borderRadius: '5px', backgroundColor: '#007bff', color: 'white', border: 'none' }}>
            Add Phone Number
          </button>
        </div>

        {/* Email Addresses Section */}
        <div style={{ marginTop: '20px', textAlign: 'center' }}>
          <h3>Email Addresses</h3>
          {newPatient.emailAddresses.map((emailAddress, index) => (
            <div key={index} style={{ display: 'flex', justifyContent: 'center', gap: '10px', marginBottom: '10px' }}>
              <input
                type="text"
                name="emailAddress"
                placeholder="Email Address"
                value={emailAddress.emailAddress}
                onChange={(e) => handleEmailAddressChange(index, e)}
                style={{ padding: '10px', borderRadius: '5px', width: '300px' }}
              />
              <select
                name="emailType"
                value={emailAddress.emailType}
                onChange={(e) => handleEmailAddressChange(index, e)}
                style={{ padding: '10px', borderRadius: '5px', width: '150px' }}
              >
                <option value="Personal">Personal</option>
                <option value="Work">Work</option>
              </select>
              <button
                onClick={() => removeEmailAddressField(index)}
                style={{ padding: '10px 20px', borderRadius: '5px', backgroundColor: '#dc3545', color: 'white', border: 'none' }}
              >
                Remove
              </button>
            </div>
          ))}
          <button onClick={addEmailAddressField} style={{ padding: '10px 20px', borderRadius: '5px', backgroundColor: '#007bff', color: 'white', border: 'none' }}>
            Add Email Address
          </button>
        </div>

        <div style={{ marginTop: '20px', textAlign: 'center' }}>
          <button
            onClick={handleAddPatient}
            style={{ padding: '10px 20px', borderRadius: '5px', backgroundColor: '#28a745', color: 'white', border: 'none' }}
          >
            Add Patient
          </button>
        </div>
      </div>

      {/* Patient List */}
      <table style={{ width: '100%', borderCollapse: 'collapse', textAlign: 'left' }}>
        <thead>
          <tr>
            <th style={{ borderBottom: '2px solid #ccc', padding: '10px' }}>First Name</th>
            <th style={{ borderBottom: '2px solid #ccc', padding: '10px' }}>Middle Name</th>
            <th style={{ borderBottom: '2px solid #ccc', padding: '10px' }}>Last Name</th>
            <th style={{ borderBottom: '2px solid #ccc', padding: '10px' }}>Date of Birth</th>
            <th style={{ borderBottom: '2px solid #ccc', padding: '10px' }}>Gender</th>
            <th style={{ borderBottom: '2px solid #ccc', padding: '10px' }}>Address</th>
            <th style={{ borderBottom: '2px solid #ccc', padding: '10px' }}>TCKN</th>
            <th style={{ borderBottom: '2px solid #ccc', padding: '10px' }}>Passport Number</th>
            <th style={{ borderBottom: '2px solid #ccc', padding: '10px' }}>Phone Numbers</th>
            <th style={{ borderBottom: '2px solid #ccc', padding: '10px' }}>Email Addresses</th>
            <th style={{ borderBottom: '2px solid #ccc', padding: '10px' }}>SMS Active</th>
            <th style={{ borderBottom: '2px solid #ccc', padding: '10px' }}>Email Active</th>
            <th style={{ borderBottom: '2px solid #ccc', padding: '10px' }}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {patients.map(patient => (
            <tr key={patient.id}>
              <td style={{ borderBottom: '1px solid #ccc', padding: '10px' }}>{patient.firstName}</td>
              <td style={{ borderBottom: '1px solid #ccc', padding: '10px' }}>{patient.middleName}</td>
              <td style={{ borderBottom: '1px solid #ccc', padding: '10px' }}>{patient.lastName}</td>
              <td style={{ borderBottom: '1px solid #ccc', padding: '10px' }}>{formatDateOfBirth(patient.dateOfBirth)}</td>
              <td style={{ borderBottom: '1px solid #ccc', padding: '10px' }}>{patient.gender}</td>
              <td style={{ borderBottom: '1px solid #ccc', padding: '10px' }}>{patient.address}</td>
              <td style={{ borderBottom: '1px solid #ccc', padding: '10px' }}>{patient.tckn}</td>
              <td style={{ borderBottom: '1px solid #ccc', padding: '10px' }}>{patient.passportNumber}</td>
              <td style={{ borderBottom: '1px solid #ccc', padding: '10px' }}>
                <ul>
                  {patient.phoneNumbers.map((phoneNumber, index) => (
                    <li key={index}>
                      {phoneNumber.phoneNumber} ({phoneNumber.phoneType})
                    </li>
                  ))}
                </ul>
              </td>
              <td style={{ borderBottom: '1px solid #ccc', padding: '10px' }}>
                <ul>
                  {patient.emailAddresses.map((emailAddress, index) => (
                    <li key={index}>
                      {emailAddress.emailAddress} ({emailAddress.emailType})
                    </li>
                  ))}
                </ul>
              </td>
              <td style={{ borderBottom: '1px solid #ccc', padding: '10px' }}>{patient.isSmsActive ? 'Yes' : 'No'}</td>
              <td style={{ borderBottom: '1px solid #ccc', padding: '10px' }}>{patient.isEmailActive ? 'Yes' : 'No'}</td>
              <td style={{ borderBottom: '1px solid #ccc', padding: '10px' }}>
                <button
                  onClick={() => handleDeletePatient(patient.id)}
                  style={{ padding: '5px 10px', borderRadius: '5px', backgroundColor: '#dc3545', color: 'white', border: 'none' }}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default PatientForm;
