import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const HospitalPage = () => {
  const [patients, setPatients] = useState([]);
  const [newPatient, setNewPatient] = useState({
    id: '', // ID field added
    firstName: '',
    middleName: '',
    lastName: '',
    dateOfBirth: '',
    gender: '',
    address: '',
    tckn: '',
    passportNumber: '',
    isSmsActive: false,
    isEmailActive: false,
    phoneNumbers: [],
    emailAddresses: []
  });
  const [errors, setErrors] = useState({});
  const [popupMessage, setPopupMessage] = useState('');

  useEffect(() => {
    fetchPatients();
  }, []);

  const fetchPatients = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/patient/getall');
      setPatients(response.data);
    } catch (error) {
      console.error('Error fetching patients:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name === 'tckn' && isNaN(value)) return;
    setNewPatient(prevState => ({ ...prevState, [name]: value }));
  };

  const handlePhoneNumberChange = (index, e) => {
    const { name, value } = e.target;
    const updatedPhoneNumbers = [...newPatient.phoneNumbers];
    updatedPhoneNumbers[index][name] = value;
    setNewPatient(prevState => ({ ...prevState, phoneNumbers: updatedPhoneNumbers }));
  };

  const handleEmailAddressChange = (index, e) => {
    const { name, value } = e.target;
    const updatedEmailAddresses = [...newPatient.emailAddresses];
    updatedEmailAddresses[index][name] = value;
    setNewPatient(prevState => ({ ...prevState, emailAddresses: updatedEmailAddresses }));
  };

  const handleCheckboxChange = (e) => {
    const { name, checked } = e.target;
    setNewPatient(prevState => ({ ...prevState, [name]: checked }));
  };

  const validateFields = () => {
    let tempErrors = {};
    if (!newPatient.firstName) tempErrors.firstName = 'First Name is required';
    if (!newPatient.lastName) tempErrors.lastName = 'Last Name is required';
    if (!newPatient.dateOfBirth) tempErrors.dateOfBirth = 'Date of Birth is required';
    if (!newPatient.gender || newPatient.gender.length !== 1) tempErrors.gender = 'Gender must be 1 character';
    if (!newPatient.address) tempErrors.address = 'Address is required';
    if (!newPatient.tckn || newPatient.tckn.length !== 11) tempErrors.tckn = 'TCKN must be 11 digits';
    if (!newPatient.passportNumber) tempErrors.passportNumber = 'Passport Number is required';
    setErrors(tempErrors);

    return Object.keys(tempErrors).length === 0;
  };

  const handleAddPatient = async () => {
    if (!validateFields()) {
      setPopupMessage('Please correct the errors before submitting.');
      setTimeout(() => setPopupMessage(''), 3000);
      return;
    }

    try {
      const response = await axios.post('http://localhost:8080/api/patient/addPatient', newPatient);
      setPatients([...patients, response.data]);
      setNewPatient({
        id: '', // Reset ID field
        firstName: '',
        middleName: '',
        lastName: '',
        dateOfBirth: '',
        gender: '',
        address: '',
        tckn: '',
        passportNumber: '',
        isSmsActive: false,
        isEmailActive: false,
        phoneNumbers: [],
        emailAddresses: []
      });
      setErrors({});
      setPopupMessage('Patient added successfully!');
      setTimeout(() => setPopupMessage(''), 3000);
    } catch (error) {
      console.error('Error adding patient:', error);
      setPopupMessage('An error occurred while adding the patient.');
      setTimeout(() => setPopupMessage(''), 3000);
    }
  };

  const handleUpdatePatient = async (id) => {
    if (!validateFields()) {
        setPopupMessage('Please correct the errors before submitting.');
        setTimeout(() => setPopupMessage(''), 3000);
        return;
    }

    try {
        const response = await axios.put(`http://localhost:8080/api/patient/update/${id}`, newPatient);
        const updatedPatients = patients.map(patient =>
            patient.id === id ? response.data : patient
        );
        setPatients(updatedPatients);

        // Reset form
        setNewPatient({
            id: '', // Reset ID field
            firstName: '',
            middleName: '',
            lastName: '',
            dateOfBirth: '',
            gender: '',
            address: '',
            tckn: '',
            passportNumber: '',
            isSmsActive: false,
            isEmailActive: false,
            phoneNumbers: [],
            emailAddresses: []
        });
        setErrors({});
        setPopupMessage('Patient updated successfully!');
        setTimeout(() => setPopupMessage(''), 3000);
    } catch (error) {
        console.error('Error updating patient:', error);
        setPopupMessage('An error occurred while updating the patient.');
        setTimeout(() => setPopupMessage(''), 3000);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:8080/api/patient/delete/${id}`);
      setPatients(patients.filter(patient => patient.id !== id));
      setPopupMessage('Patient deleted successfully!');
      setTimeout(() => setPopupMessage(''), 3000);
    } catch (error) {
      console.error('Error deleting patient:', error);
      setPopupMessage('An error occurred while deleting the patient.');
      setTimeout(() => setPopupMessage(''), 3000);
    }
  };

  const addPhoneNumberField = () => {
    if (newPatient.phoneNumbers.length < 3) {
      setNewPatient(prevState => ({
        ...prevState,
        phoneNumbers: [...prevState.phoneNumbers, { phoneNumber: '', phoneType: 'mobile' }]
      }));
    }
  };

  const removePhoneNumberField = (index) => {
    setNewPatient(prevState => ({
      ...prevState,
      phoneNumbers: prevState.phoneNumbers.filter((_, i) => i !== index)
    }));
  };

  const addEmailAddressField = () => {
    if (newPatient.emailAddresses.length < 3) {
      setNewPatient(prevState => ({
        ...prevState,
        emailAddresses: [...prevState.emailAddresses, { emailAddress: '', emailType: 'Personal' }]
      }));
    }
  };

  const removeEmailAddressField = (index) => {
    setNewPatient(prevState => ({
      ...prevState,
      emailAddresses: prevState.emailAddresses.filter((_, i) => i !== index)
    }));
  };

  return (
    <div style={{ textAlign: 'center', margin: '0 auto', width: '80%' }}>
      {/* Popup Message */}
      {popupMessage && (
        <div style={{
          position: 'fixed',
          top: '10px',
          right: '10px',
          backgroundColor: '#f8d7da',
          color: '#721c24',
          border: '1px solid #f5c6cb',
          borderRadius: '5px',
          padding: '10px',
          zIndex: 1000
        }}>
          {popupMessage}
        </div>
      )}

      <Link to="/notifications" style={{ float: 'right', fontSize: '18px', marginRight: '20px', textDecoration: 'none', color: '#007bff' }}>
        Go to Notification Page
      </Link>

      <h1 style={{ marginBottom: '20px', fontSize: '36px' }}>Hospital Patient Management</h1>

      {/* Add Patient Form */}
      <div style={{ marginBottom: '40px', padding: '20px', border: '1px solid #ccc', borderRadius: '10px', backgroundColor: '#f9f9f9' }}>
        <h2 style={{ marginBottom: '20px' }}>Add New Patient</h2>
        <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center', gap: '10px' }}>
          <input
            type="text"
            name="id"
            placeholder="ID" // ID field added
            value={newPatient.id}
            onChange={handleInputChange}
            style={{ borderColor: errors.id ? 'red' : '#ccc', padding: '10px', borderRadius: '5px', width: '220px' }}
          />
          <input
            type="text"
            name="firstName"
            placeholder="First Name"
            value={newPatient.firstName}
            onChange={handleInputChange}
            style={{ borderColor: errors.firstName ? 'red' : '#ccc', padding: '10px', borderRadius: '5px', width: '220px' }}
          />
          <input
            type="text"
            name="middleName"
            placeholder="Middle Name"
            value={newPatient.middleName}
            onChange={handleInputChange}
            style={{ borderColor: errors.middleName ? 'red' : '#ccc', padding: '10px', borderRadius: '5px', width: '220px' }}
          />
          <input
            type="text"
            name="lastName"
            placeholder="Last Name"
            value={newPatient.lastName}
            onChange={handleInputChange}
            style={{ borderColor: errors.lastName ? 'red' : '#ccc', padding: '10px', borderRadius: '5px', width: '220px' }}
          />
          <input
            type="date"
            name="dateOfBirth"
            value={newPatient.dateOfBirth}
            onChange={handleInputChange}
            style={{ borderColor: errors.dateOfBirth ? 'red' : '#ccc', padding: '10px', borderRadius: '5px', width: '220px' }}
          />
          <input
            type="text"
            name="gender"
            placeholder="Gender (M/F)"
            value={newPatient.gender}
            onChange={handleInputChange}
            style={{ borderColor: errors.gender ? 'red' : '#ccc', padding: '10px', borderRadius: '5px', width: '220px' }}
          />
          <input
            type="text"
            name="address"
            placeholder="Address"
            value={newPatient.address}
            onChange={handleInputChange}
            style={{ borderColor: errors.address ? 'red' : '#ccc', padding: '10px', borderRadius: '5px', width: '220px' }}
          />
          <input
            type="text"
            name="tckn"
            placeholder="TCKN"
            value={newPatient.tckn}
            onChange={handleInputChange}
            style={{ borderColor: errors.tckn ? 'red' : '#ccc', padding: '10px', borderRadius: '5px', width: '220px' }}
          />
          <input
            type="text"
            name="passportNumber"
            placeholder="Passport Number"
            value={newPatient.passportNumber}
            onChange={handleInputChange}
            style={{ borderColor: errors.passportNumber ? 'red' : '#ccc', padding: '10px', borderRadius: '5px', width: '220px' }}
          />
          <div>
            <label>
              <input
                type="checkbox"
                name="isSmsActive"
                checked={newPatient.isSmsActive}
                onChange={handleCheckboxChange}
                style={{ marginRight: '10px' }}
              />
              SMS Active
            </label>
          </div>
          <div>
            <label>
              <input
                type="checkbox"
                name="isEmailActive"
                checked={newPatient.isEmailActive}
                onChange={handleCheckboxChange}
                style={{ marginRight: '10px' }}
              />
              Email Active
            </label>
          </div>
        </div>

        {/* Phone Numbers */}
        <div style={{ marginTop: '20px' }}>
          <h3>Phone Numbers</h3>
          {newPatient.phoneNumbers.map((phoneNumber, index) => (
            <div key={index} style={{ display: 'flex', gap: '10px', justifyContent: 'center', alignItems: 'center', marginBottom: '10px' }}>
              <input
                type="text"
                name="phoneNumber"
                placeholder="Phone Number"
                value={phoneNumber.phoneNumber}
                onChange={(e) => handlePhoneNumberChange(index, e)}
                style={{ padding: '10px', borderRadius: '5px', width: '220px' }}
              />
              <select
                name="phoneType"
                value={phoneNumber.phoneType}
                onChange={(e) => handlePhoneNumberChange(index, e)}
                style={{ padding: '10px', borderRadius: '5px', width: '220px' }}
              >
                <option value="mobile">Mobile</option>
                <option value="home">Home</option>
                <option value="work">Work</option>
              </select>
              <button onClick={() => removePhoneNumberField(index)} style={{ padding: '10px', borderRadius: '5px', backgroundColor: '#dc3545', color: '#fff', border: 'none' }}>
                Remove
              </button>
            </div>
          ))}
          <button onClick={addPhoneNumberField} style={{ padding: '10px', borderRadius: '5px', backgroundColor: '#007bff', color: '#fff', border: 'none', marginTop: '10px' }}>
            Add Phone Number
          </button>
        </div>

        {/* Email Addresses */}
        <div style={{ marginTop: '20px' }}>
          <h3>Email Addresses</h3>
          {newPatient.emailAddresses.map((emailAddress, index) => (
            <div key={index} style={{ display: 'flex', gap: '10px', justifyContent: 'center', alignItems: 'center', marginBottom: '10px' }}>
              <input
                type="text"
                name="emailAddress"
                placeholder="Email Address"
                value={emailAddress.emailAddress}
                onChange={(e) => handleEmailAddressChange(index, e)}
                style={{ padding: '10px', borderRadius: '5px', width: '220px' }}
              />
              <select
                name="emailType"
                value={emailAddress.emailType}
                onChange={(e) => handleEmailAddressChange(index, e)}
                style={{ padding: '10px', borderRadius: '5px', width: '220px' }}
              >
                <option value="Personal">Personal</option>
                <option value="Work">Work</option>
                <option value="Other">Other</option>
              </select>
              <button onClick={() => removeEmailAddressField(index)} style={{ padding: '10px', borderRadius: '5px', backgroundColor: '#dc3545', color: '#fff', border: 'none' }}>
                Remove
              </button>
            </div>
          ))}
          <button onClick={addEmailAddressField} style={{ padding: '10px', borderRadius: '5px', backgroundColor: '#007bff', color: '#fff', border: 'none', marginTop: '10px' }}>
            Add Email Address
          </button>
        </div>

        <div style={{ marginTop: '30px' }}>
          <button onClick={handleAddPatient} style={{ padding: '15px 30px', borderRadius: '5px', backgroundColor: '#28a745', color: '#fff', border: 'none', fontSize: '18px' }}>
            Add Patient
          </button>
        </div>
      </div>

      {/* Display Patient List */}
      <div style={{ marginTop: '20px' }}>
        <h2>Patient List</h2>
        <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '20px' }}>
          <thead>
            <tr>
              <th style={{ border: '1px solid #ccc', padding: '10px' }}>ID</th> {/* ID column added */}
              <th style={{ border: '1px solid #ccc', padding: '10px' }}>First Name</th>
              <th style={{ border: '1px solid #ccc', padding: '10px' }}>Middle Name</th>
              <th style={{ border: '1px solid #ccc', padding: '10px' }}>Last Name</th>
              <th style={{ border: '1px solid #ccc', padding: '10px' }}>Date of Birth</th>
              <th style={{ border: '1px solid #ccc', padding: '10px' }}>Gender</th>
              <th style={{ border: '1px solid #ccc', padding: '10px' }}>Address</th>
              <th style={{ border: '1px solid #ccc', padding: '10px' }}>TCKN</th>
              <th style={{ border: '1px solid #ccc', padding: '10px' }}>Passport Number</th>
              <th style={{ border: '1px solid #ccc', padding: '10px' }}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {patients.map(patient => (
              <tr key={patient.id}>
                <td style={{ border: '1px solid #ccc', padding: '10px' }}>{patient.id}</td> {/* ID field displayed */}
                <td style={{ border: '1px solid #ccc', padding: '10px' }}>{patient.firstName}</td>
                <td style={{ border: '1px solid #ccc', padding: '10px' }}>{patient.middleName}</td>
                <td style={{ border: '1px solid #ccc', padding: '10px' }}>{patient.lastName}</td>
                <td style={{ border: '1px solid #ccc', padding: '10px' }}>{patient.dateOfBirth}</td>
                <td style={{ border: '1px solid #ccc', padding: '10px' }}>{patient.gender}</td>
                <td style={{ border: '1px solid #ccc', padding: '10px' }}>{patient.address}</td>
                <td style={{ border: '1px solid #ccc', padding: '10px' }}>{patient.tckn}</td>
                <td style={{ border: '1px solid #ccc', padding: '10px' }}>{patient.passportNumber}</td>
                <td style={{ border: '1px solid #ccc', padding: '10px', display: 'flex', gap: '10px', justifyContent: 'center' }}>
                  <button onClick={() => handleEditPatient(patient)} style={{ padding: '10px', borderRadius: '5px', backgroundColor: '#ffc107', color: '#fff', border: 'none' }}>
                    Edit
                  </button>
                  <button onClick={() => handleDeletePatient(patient.id)} style={{ padding: '10px', borderRadius: '5px', backgroundColor: '#dc3545', color: '#fff', border: 'none' }}>
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default PatientForm;
