import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom'; // React Router'dan Link bileÅŸeni

const HospitalPage = () => {
  const [patients, setPatients] = useState([]);
  const [newPatient, setNewPatient] = useState({
    firstName: '',
    middleName: '',
    lastName: '',
    dateOfBirth: '',
    gender: '',
    address: '',
    tckn: '',
    passportNumber: '',
    isSmsActive: false,
    isEmailActive: false,
    phoneNumbers: [],
    emailAddresses: []
  });
  const [errors, setErrors] = useState({});
  const [popupMessage, setPopupMessage] = useState('');

  useEffect(() => {
    fetchPatients();
  }, []);

  const fetchPatients = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/patient/getall');
      setPatients(response.data);
      console.log(response.data);
    } catch (error) {
      console.error('Error fetching patients:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;

    if (name === 'tckn' && isNaN(value)) {
      return;
    }

    setNewPatient(prevState => ({ ...prevState, [name]: value }));
  };

  const handleCheckboxChange = (e) => {
    const { name, checked } = e.target;
    setNewPatient(prevState => ({ ...prevState, [name]: checked }));
  };

  const handlePhoneChange = (index, e) => {
    const { name, value } = e.target;
    const updatedPhones = [...newPatient.phoneNumbers];
    updatedPhones[index] = { ...updatedPhones[index], [name]: value };
    setNewPatient(prevState => ({ ...prevState, phoneNumbers: updatedPhones }));
  };

  const handleEmailChange = (index, e) => {
    const { name, value } = e.target;
    const updatedEmails = [...newPatient.emailAddresses];
    updatedEmails[index] = { ...updatedEmails[index], [name]: value };
    setNewPatient(prevState => ({ ...prevState, emailAddresses: updatedEmails }));
  };

  const addPhoneNumber = () => {
    if (newPatient.phoneNumbers.length < 3) {
      setNewPatient(prevState => ({ ...prevState, phoneNumbers: [...prevState.phoneNumbers, { phoneNumber: '', phoneType: 'mobile' }] }));
    }
  };

  const removePhoneNumber = (index) => {
    const updatedPhones = newPatient.phoneNumbers.filter((_, i) => i !== index);
    setNewPatient(prevState => ({ ...prevState, phoneNumbers: updatedPhones }));
  };

  const addEmailAddress = () => {
    if (newPatient.emailAddresses.length < 3) {
      setNewPatient(prevState => ({ ...prevState, emailAddresses: [...prevState.emailAddresses, { emailAddress: '', emailType: 'Personal' }] }));
    }
  };

  const removeEmailAddress = (index) => {
    const updatedEmails = newPatient.emailAddresses.filter((_, i) => i !== index);
    setNewPatient(prevState => ({ ...prevState, emailAddresses: updatedEmails }));
  };

  const validateFields = () => {
    let tempErrors = {};
    if (!newPatient.firstName) tempErrors.firstName = 'First Name is required';
    if (!newPatient.lastName) tempErrors.lastName = 'Last Name is required';
    if (!newPatient.dateOfBirth) tempErrors.dateOfBirth = 'Date of Birth is required';
    if (!newPatient.gender || newPatient.gender.length !== 1) tempErrors.gender = 'Gender must be 1 character';
    if (!newPatient.address) tempErrors.address = 'Address is required';
    if (!newPatient.tckn || newPatient.tckn.length !== 11) tempErrors.tckn = 'TCKN must be 11 digits';
    if (!newPatient.passportNumber) tempErrors.passportNumber = 'Passport Number is required';
    setErrors(tempErrors);
    return Object.keys(tempErrors).length === 0;
  };

  const handleAddPatient = async () => {
    if (!validateFields()) {
      setPopupMessage('Please correct the errors before submitting.');
      setTimeout(() => setPopupMessage(''), 3000);
      return;
    }

    try {
      const patientToAdd = {
        ...newPatient,
        phoneNumbers: newPatient.phoneNumbers.map(phone => ({
          phoneNumber: phone.phoneNumber,
          phoneType: phone.phoneType
        })),
        emailAddresses: newPatient.emailAddresses.map(email => ({
          emailAddress: email.emailAddress,
          emailType: email.emailType
        }))
      };

      const response = await axios.post('http://localhost:8080/api/patient/addPatient', patientToAdd);
      setPatients([...patients, response.data]);
      setNewPatient({
        firstName: '',
        middleName: '',
        lastName: '',
        dateOfBirth: '',
        gender: '',
        address: '',
        tckn: '',
        passportNumber: '',
        isSmsActive: false,
        isEmailActive: false,
        phoneNumbers: [],
        emailAddresses: []
      });
      setErrors({});
      setPopupMessage('Patient added successfully!');
      setTimeout(() => setPopupMessage(''), 3000);
    } catch (error) {
      console.error('Error adding patient:', error);
      setPopupMessage('An error occurred while adding the patient.');
      setTimeout(() => setPopupMessage(''), 3000);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:8080/api/patient/delete/${id}`);
      setPatients(patients.filter(patient => patient.id !== id));
      setPopupMessage('Patient deleted successfully!');
      setTimeout(() => setPopupMessage(''), 3000);
    } catch (error) {
      console.error('Error deleting patient:', error);
      setPopupMessage('An error occurred while deleting the patient.');
      setTimeout(() => setPopupMessage(''), 3000);
    }
  };

  return (
    <div style={{ textAlign: 'center' }}>
      {/* Popup Message */}
      {popupMessage && (
        <div style={{
          position: 'fixed',
          top: '10px',
          right: '10px',
          backgroundColor: '#f8d7da',
          color: '#721c24',
          border: '1px solid #f5c6cb',
          borderRadius: '5px',
          padding: '10px',
          zIndex: 1000
        }}>
          {popupMessage}
        </div>
      )}

      <Link to="/notifications" style={{ float: 'right', fontSize: '18px', marginRight: '20px' }}>
        Go to Notification Page
      </Link>

      <h1>Hospital Patient Management</h1>

      {/* Add Patient Form */}
      <div style={{ margin: '0 auto', width: '60%' }}>
        <h2>Add New Patient</h2>
        <input
          type="text"
          name="firstName"
          placeholder="First Name"
          value={newPatient.firstName}
          onChange={handleInputChange}
          style={{ borderColor: errors.firstName ? 'red' : '' }}
        />
        <input
          type="text"
          name="middleName"
          placeholder="Middle Name"
          value={newPatient.middleName}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="lastName"
          placeholder="Last Name"
          value={newPatient.lastName}
          onChange={handleInputChange}
          style={{ borderColor: errors.lastName ? 'red' : '' }}
        />
        <input
          type="date"
          name="dateOfBirth"
          placeholder="Date of Birth"
          value={newPatient.dateOfBirth}
          onChange={handleInputChange}
          style={{ borderColor: errors.dateOfBirth ? 'red' : '' }}
        />
        <input
          type="text"
          name="gender"
          placeholder="Gender"
          value={newPatient.gender}
          onChange={handleInputChange}
          style={{ borderColor: errors.gender ? 'red' : '' }}
        />
        <input
          type="text"
          name="address"
          placeholder="Address"
          value={newPatient.address}
          onChange={handleInputChange}
          style={{ borderColor: errors.address ? 'red' : '' }}
        />
        <input
          type="text"
          name="tckn"
          placeholder="TCKN"
          value={newPatient.tckn}
          onChange={handleInputChange}
          style={{ borderColor: errors.tckn ? 'red' : '' }}
        />
        <input
          type="text"
          name="passportNumber"
          placeholder="Passport Number"
          value={newPatient.passportNumber}
          onChange={handleInputChange}
          style={{ borderColor: errors.passportNumber ? 'red' : '' }}
        />

        <div>
          <label>
            <input
              type="checkbox"
              name="isSmsActive"
              checked={newPatient.isSmsActive}
              onChange={handleCheckboxChange}
            />
            SMS Notifications
          </label>
          <label>
            <input
              type="checkbox"
              name="isEmailActive"
              checked={newPatient.isEmailActive}
              onChange={handleCheckboxChange}
            />
            Email Notifications
          </label>
        </div>

        <div>
          <h3>Phone Numbers</h3>
          {newPatient.phoneNumbers.map((phone, index) => (
            <div key={index} style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
              <input
                type="text"
                name="phoneNumber"
                placeholder="Phone Number"
                value={phone.phoneNumber}
                onChange={(e) => handlePhoneChange(index, e)}
                style={{ marginRight: '10px', borderColor: '' }}
              />
              <select
                name="phoneType"
                value={phone.phoneType}
                onChange={(e) => handlePhoneChange(index, e)}
                style={{ marginRight: '10px' }}
              >
                <option value="mobile">Mobile</option>
                <option value="home">Home</option>
                <option value="work">Work</option>
              </select>
              <button onClick={() => removePhoneNumber(index)}>Remove</button>
            </div>
          ))}
          {newPatient.phoneNumbers.length < 3 && (
            <button onClick={addPhoneNumber}>Add Phone Number</button>
          )}
        </div>

        <div>
          <h3>Email Addresses</h3>
          {newPatient.emailAddresses.map((email, index) => (
            <div key={index} style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
              <input
                type="text"
                name="emailAddress"
                placeholder="Email Address"
                value={email.emailAddress}
                onChange={(e) => handleEmailChange(index, e)}
                style={{ marginRight: '10px', borderColor: '' }}
              />
              <select
                name="emailType"
                value={email.emailType}
                onChange={(e) => handleEmailChange(index, e)}
                style={{ marginRight: '10px' }}
              >
                <option value="personal">Personal</option>
                <option value="work">Work</option>
              </select>
              <button onClick={() => removeEmailAddress(index)}>Remove</button>
            </div>
          ))}
          {newPatient.emailAddresses.length < 3 && (
            <button onClick={addEmailAddress}>Add Email Address</button>
          )}
        </div>

        <button onClick={handleAddPatient}>Add Patient</button>
      </div>

      {/* Patient Table */}
      <div style={{ margin: '0 auto', width: '80%' }}>
        <h2>Patient List</h2>
        <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '20px' }}>
          <thead>
            <tr style={{ backgroundColor: '#f2f2f2' }}>
              <th style={{ border: '1px solid #ddd', padding: '8px' }}>First Name</th>
              <th style={{ border: '1px solid #ddd', padding: '8px' }}>Middle Name</th>
              <th style={{ border: '1px solid #ddd', padding: '8px' }}>Last Name</th>
              <th style={{ border: '1px solid #ddd', padding: '8px' }}>Date of Birth</th>
              <th style={{ border: '1px solid #ddd', padding: '8px' }}>Gender</th>
              <th style={{ border: '1px solid #ddd', padding: '8px' }}>Address</th>
              <th style={{ border: '1px solid #ddd', padding: '8px' }}>TCKN</th>
              <th style={{ border: '1px solid #ddd', padding: '8px' }}>Passport Number</th>
              <th style={{ border: '1px solid #ddd', padding: '8px' }}>Phone Numbers</th>
              <th style={{ border: '1px solid #ddd', padding: '8px' }}>Email Addresses</th>
              <th style={{ border: '1px solid #ddd', padding: '8px' }}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {patients.map(patient => (
              <tr key={patient.id}>
                <td style={{ border: '1px solid #ddd', padding: '8px' }}>{patient.firstName}</td>
                <td style={{ border: '1px solid #ddd', padding: '8px' }}>{patient.middleName}</td>
                <td style={{ border: '1px solid #ddd', padding: '8px' }}>{patient.lastName}</td>
                <td style={{ border: '1px solid #ddd', padding: '8px' }}>
                  {new Date(patient.dateOfBirth).toLocaleDateString('en-GB')}
                </td>
                <td style={{ border: '1px solid #ddd', padding: '8px' }}>{patient.gender}</td>
                <td style={{ border: '1px solid #ddd', padding: '8px' }}>{patient.address}</td>
                <td style={{ border: '1px solid #ddd', padding: '8px' }}>{patient.tckn}</td>
                <td style={{ border: '1px solid #ddd', padding: '8px' }}>{patient.passportNumber}</td>
                <td style={{ border: '1px solid #ddd', padding: '8px', fontWeight: 'bold' }}>
                  {patient.phoneNumbers.map(phone => (
                    <div key={phone.phoneNumber}>{phone.phoneNumber}</div>
                  ))}
                </td>
                <td style={{ border: '1px solid #ddd', padding: '8px', fontWeight: 'bold' }}>
                  {patient.emailAddresses.map(email => (
                    <div key={email.emailAddress}>{email.emailAddress}</div>
                  ))}
                </td>
                <td style={{ border: '1px solid #ddd', padding: '8px' }}>
                  <button onClick={() => handleDelete(patient.id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default HospitalPage;
